import React from 'react';
import NavigatorPages from 'navigator-cordova-reactjs';

//----Pages----//
import Home from "./pages/home";

import { ErrorConnection } from './error';

//----style----//
//---//
//"style.css" it's important style to mobile view (android ios); 
import "./css/style.css";
//---//

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , screenPages: []
      , historyPages: ["home"]
      , menuOpen: false
      , showSwalLater: false
      , player: null
      , playerOpen: false
      , errorConnection: false
      , recomendItems: []
    }
    this.NavigatorPages={state:{historyPages:[""]}};

    this.full_Height = window.innerHeight;


    this.bezy = false;
    // this.listLevelPages = [];
    // this.listLevelPages["home"] = 0;
    // this.listLevelPages["broadcastSchedule"] = 1;
    // this.listLevelPages["itemsByCategory"] = 1;
    // this.listLevelPages["livePlay"] = 9;
    // this.listLevelPages["category"] = 2;
    // this.listLevelPages["search"] = 8;
    // this.listLevelPages["programs"] = 1;
    // this.listLevelPages["program"] = 2;


    this.media = null;
    this.MusicControls = null;

    //  this.playPause = this.playPause.bind(this);


  }

  componentDidMount() {
    //debugger

  }

  openMenu() {
    this.setState({ menuOpen: true });
  }
  closeMenu() {
    this.setState({ menuOpen: false });
  }

  // async CloseApp() {
  //   // if (fthis.state.playerMusicControls) {
  //   //   window.MusicControls.destroy();
  //   //   this.setState({ playerMusicControls: false });
  //   // }
  //   if (window.media !== undefined){
  //     console.log('window.media !== undefined');
  //     window.media.stop();
  //   }else{
  //     console.log('window.media === undefined');
  //   }

  //   this.bezy = false;
  //   return;
  // }

  componentWillMount() {
    // const fthis = this;
    // if (this.state.playerHistory !== false) {
    //   this.state.player.playerPlayNew(this.state.playerHistory.item);
    // }

    // document.addEventListener("offline", () => { fthis.setState({ errorConnection: true }); }, false);
    // document.addEventListener("online", () => { fthis.setState({ errorConnection: false }); }, false);

    window.closeOrBack = () => {
      document.getElementById("NavigatorPagesBack").click();
    }
    //
    if (window.cordova)
      window.plugins.OnDestroyPlugin.setEventListener((event) => {
        console.log('OnDestroyPlugin');

        // fthis.CloseApp();
        // console.log('OnDestroyPluginEnd');
      });

    //---נעילת מסך
    window.screen.orientation.lock('portrait');

    //--כפתור חזרה לאנדרואיד
    document.addEventListener("backbutton"
      , (e) => {
        window.closeOrBack();


        //  alert("Android back button"+JSON.stringify(e));
        // NavigatorPages.app.exitApp();
      }
      , false);

  }


  render() {
    // debugger
    const fthis = this;
    let header_Height = 52;
    let bottom_Height = this.state.playerOpen ? 84 : 0;

    let body_Height = this.full_Height - (header_Height + bottom_Height);

    let nowPage = this.NavigatorPages.state.historyPages[this.NavigatorPages.state.historyPages.length - 1];
    return (
      fthis.state.errorConnection ?
        <ErrorConnection />
        : [
          <NavigatorPages key="NavigatorPages" myApp={this}
            homePageName={"home"}
            listLevelPages={{
              "home": 0,
              "broadcastSchedule": 1,
              "itemsByCategory": 1,
              "livePlay": 9,
              "category": 2,
              "search": 8,
              "programs": 1,
              "program": 2,
            }} />


          , <Swal key="swal" myApp={this} />

          , <div key="body" className="noScroll body" style={{ //width: window.innerWidth, 
            height: this.full_Height + "px"
          }}>
            {[
              /* //--header--// */
              <div key="top_section" className="noScroll top_section" style={{ height: header_Height + "px" }}>
                <div className="noScroll navbar w-nav">
                  <CheeseburgerMenu
                    topOffset={header_Height}
                    bottomOffset={bottom_Height + 0.5}
                    right={true}
                    width={230}
                    isOpen={this.state.menuOpen}
                    closeCallback={this.closeMenu.bind(this)}>
                    <MenuContent myParent={this} closeCallback={this.closeMenu.bind(this)} />
                  </CheeseburgerMenu>
                  <div style={{ height: header_Height ,width: "64px" }} className="hamburger">
                    <HamburgerMenu
                      isOpen={this.state.menuOpen}
                      menuClicked={
                        this.state.menuOpen
                          ? this.closeMenu.bind(this)
                          : this.openMenu.bind(this)}
                      width={36}
                      height={20}
                      strokeWidth={3}
                      rotate={0}
                      color="#000000"//'#31b1ca'
                      borderRadius={0}
                      animationDuration={0.5}
                    />
                  </div>
                </div>
                <div onClick={() => {
                  //debugger
                  if (nowPage === "home") {
                    if (window.media !== undefined)
                      window.media.stop();
                    window.location.reload();
                  }
                  else {
                    fthis.NavigatorPages.ChangePage('home', null);
                    fthis.NavigatorPages.setState({ historyPages: ["home"] });
                  }
                }} className="top_logo w-inline-block">
                  {/* fthis.ChangePage('home', null); fthis.setState({ historyPages: ["home"] });  */}
                  <img src="images/logo_radio_turkiz.svg" alt="" /></div>
                <div onClick={() => { fthis.NavigatorPages.ChangePage("livePlay", null); }} className="top_live_button w-inline-block">
                  <div>לשידור החי</div>
                </div>
              </div>


              //* //--body--// */
              , <div key="body_div">
                {/* //--home page--// */}
                <div key="home" id="home" style={{ height: body_Height, top: header_Height }}>
                  {
                    nowPage === "home" || fthis.NavigatorPages.state.historyPages.includes("home")
                      ? <Home myParent={this} />
                      : <div></div>
                  }
                </div>

                {/* //--schedule--// */}
                <div key="broadcastSchedule" id="broadcastSchedule" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
                  className="hiddenPage">{
                    nowPage === "broadcastSchedule" || fthis.NavigatorPages.state.historyPages.includes("broadcastSchedule")
                      ? <BroadcastSchedule myParent={this} />
                      : <div></div>
                  }
                </div>

                {/* itemsByCategory */}
                <div key="itemsByCategory" id="itemsByCategory" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
                  className="hiddenPage">{
                    nowPage === "itemsByCategory" || fthis.NavigatorPages.state.historyPages.includes("itemsByCategory")
                      ? <ItemsByCategory myParent={this} />
                      : <div></div>
                  }
                </div>
                {/* livePlay */}
                <div key="livePlay" id="livePlay" style={{ backgroundColor: "#f4f4f4", height: body_Height, top: header_Height }}
                  className="hiddenPage">{
                    nowPage === "livePlay" || fthis.NavigatorPages.state.historyPages.includes("livePlay")
                      ? <LivePlay myParent={this} />
                      : <div></div>
                  }
                </div>
                {/* Programs */}
                <div key="programs" id="programs" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
                  className="hiddenPage">{
                    (nowPage === "programs" || fthis.NavigatorPages.state.historyPages.includes("programs")) &&
                    <Programs myParent={this} />
                  }
                </div>

                {/* category */}
                <div key="category" id="category" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
                  <Category myParent={this} />
                </div>
                {/* search */}
                <div key="search" id="search" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
                  <Search myParent={this} />
                </div>
                {/* program */}
                <div key="program" id="program" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
                  <Program myParent={this} />
                </div>

              </div>
              , <div key="bottom_Height" style={{ height: bottom_Height }} />
              , <Player key="player" myApp={this} bottom_Height={bottom_Height} />
            ]}</div>
        ]
    );
  }
}





