import React from 'react';
import $ from 'jquery';
import CheeseburgerMenu from 'cheeseburger-menu';
import HamburgerMenu from 'react-hamburger-menu';
import MenuContent from './menuContent';

//--Pages
import Home from "./pages/home";
import BroadcastSchedule from './pages/broadcastSchedule';
import ItemsByCategory from './pages/itemsByCategory';
import LivePlay from './pages/livePlay';
import Category from './pages/category';
import Programs from './pages/programs';
import Program from './pages/program';

//-----styles----//
import './css/animate.css';
import "./css/style.css";

//----images---//

let interval = null;

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , screenPages: []
      , historyPages: ["home"]
      , menuOpen: false

      , playerOpen: false
      , playerPlay: false
      , playerItem: null
      , playerIsLive: false
      , playerTime: 0//שניות
    }

    this.bezy = false;
    this.listLevelPages = [];
    this.listLevelPages["home"] = 0;
    this.listLevelPages["broadcastSchedule"] = 1;
    this.listLevelPages["itemsByCategory"] = 1;
    this.listLevelPages["livePlay"] = 9;
    this.listLevelPages["category"] = 2;
    this.listLevelPages["programs"] = 1;
    this.listLevelPages["program"] = 2;


    this.ChangePage = this.ChangePage.bind(this);
    this.playPause = this.playPause.bind(this);


  }

  playPause(playItem) {

    let fthis = this;

    if (playItem === undefined) {
      this.setState({
        playerPlay: !this.state.playerPlay
      });
    }
    else {
      this.setState({
        playerOpen: true
        , playerPlay: true
        , playerTime: 0
        , playerItem: playItem
      });
    }

    if (!this.state.playerPlay) {

      let media = new window.Media(fthis.state.playerItem.show_clip_id_full_url
        , alert('mediaSuccess')
        , function (mediaError) { alert('mediaError:' + mediaError.toString()); console.log('mediaError:' + mediaError) }
        , function (mediaStatus) { alert('mediaStatus' + mediaStatus); console.log('mediaStatus' + mediaStatus) }
      );

      console.log(media);

      interval = setInterval(function () {
        if (fthis.state.playerTime <= (
          (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
          + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
            + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
        )) {
          fthis.setState({
            playerTime: fthis.state.playerTime + 1
          });
        } else {
          clearInterval(interval);
          //הקטע הסתיים
        }
      }, 1000);
    } else {
      clearInterval(interval);
    }

  }

  openMenu() {
    this.setState({ menuOpen: true });
  }
  closeMenu() {
    this.setState({ menuOpen: false });
  }

  //----navigator and animation----///
  funAnimationIn1(goToPage, fromPage) {
    //--נכנסים דף פנימה Up--//
    let callbackFun = () => {
      this.funAnimationIn2(goToPage, fromPage);
      document.getElementById(goToPage).removeEventListener("webkitAnimationEnd", callbackFun);
    };
    document.getElementById(goToPage).addEventListener("webkitAnimationEnd", callbackFun);

    this.bezy = true;
    $('#' + goToPage).removeClass('hiddenPage');
    $('#' + goToPage).addClass('scrollPage showPage');
    $('#' + fromPage).css('z-index', 0);
    $('#' + goToPage).css('z-index', 89);
  }
  funAnimationIn2(goToPage, fromPage) {
    $('#' + fromPage).css('z-index', "");
    $('#' + goToPage).css('z-index', "");
    $('#' + goToPage).css('animation', '');
    $('#' + fromPage).removeClass('showPage');
    $('#' + fromPage).removeClass('scrollPage');
    $('#' + fromPage).addClass('hiddenPage');
    this.bezy = false;
  }

  funAnimationOut1(goToPage, fromPage) {
    //--חזרה בדפים Down--// 

    let callbackFun = () => {
      this.funAnimationOut2(goToPage, fromPage);
      document.getElementById(fromPage).removeEventListener("webkitAnimationEnd", callbackFun);
    };
    document.getElementById(fromPage).addEventListener("webkitAnimationEnd", callbackFun);
    this.bezy = true;
    $('#' + goToPage).css('z-index', 0);
    $('#' + fromPage).css('z-index', 89);
    $('#' + goToPage).removeClass('hiddenPage');
    $('#' + goToPage).addClass('scrollPage showPage');
  }
  funAnimationOut2(goToPage, fromPage) {
    $('#' + fromPage).css('animation', '');
    $('#' + goToPage).css('z-index', "");
    $('#' + fromPage).css('z-index', "");
    $('#' + fromPage).removeClass('showPage');
    $('#' + fromPage).removeClass('scrollPage');
    $('#' + fromPage).addClass('hiddenPage');
    this.bezy = false;
  }


  ChangePage(goToPage, fromPage) {

    if (!this.bezy) {
      const fthis = this;

      if (fromPage == null) {
        fromPage = "" + this.state.historyPages[this.state.historyPages.length - 1] + "";
      }

      //--זמן האנימציה
      const timeAnimation = 250;//250;//ms

      if (goToPage !== fromPage) {
        //---ניהול חזרות----//
        this.bezy = true;
        //סיום האפליקציה, סגור
        if (this.state.historyPages.length === 1
          && goToPage === undefined) {
          window.navigator.app.exitApp();
          this.bezy = false;
          return;
        } else {
          ///שמור היסטוריה
          let new_historyPages = this.state.historyPages;

          if (this.listLevelPages[goToPage] >= this.listLevelPages[fromPage]) {
            //נכנסים פנימה, הוסף להסטוריה
            new_historyPages.push(goToPage);
          } else {
            //חוזרים אחורה, מחק את הדף האחרון מההיסטוריה.
            new_historyPages.splice(new_historyPages.length - 1, 1);
          }
          //שמירת שינויים בהיסטוריה
          this.setState({
            historyPages: new_historyPages
          });
        }

        //----navigator and animation----///

        if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage]) {
          //--נכנסים דף פנימה Up--//
          this.funAnimationIn1(goToPage, fromPage);

          if (this.listLevelPages[goToPage] === 1) {
            //Up from level 0 to level 1
            $('#' + goToPage).css('animation', 'slideInRight ' + timeAnimation + 'ms');

          } else { //else if (this.listLevelPages[goToPage] === 2) {
            //Up from level 1 to level 2
            $('#' + goToPage).css('animation', 'zoomIn ' + timeAnimation + 'ms');
          }
        } else {
          //--חזרה בדפים Down--//   
          this.funAnimationOut1(goToPage, fromPage);
          if (this.listLevelPages[fromPage] === 1) {
            //Down from level 1 to level 0
            $('#' + fromPage).css('animation', 'slideOutRight ' + timeAnimation + 'ms');
          }
          else { //else if (this.listLevelPages[goToPage] === 1) {
            //Down from level 2 to level 1
            $('#' + fromPage).css('animation', 'zoomOut ' + timeAnimation + 'ms');

          }
        }
        // //עיצוב כפתור חזרה
        // if (goToPage === "home") {
        //     $('#backInHeader').css('display', "none");
        // } else {
        //     $('#backInHeader').css('display', "flex");
        // }
        if (this.listLevelPages[goToPage] < this.listLevelPages[fromPage]) {
          if (fromPage === "category") {
            // $('#').scrollTop(0);
            setTimeout(() => {
              fthis.state.myChildrens.category.setState({
                error: false,
                isLoaded: false,
                items: []
                , hasError: false
                , categoryID: 0
              });
            }
              , timeAnimation);
          }
          if (fromPage === "program") {
            // $('#').scrollTop(0);
            setTimeout(() => {
              fthis.state.myChildrens.program.setState({
                error: false,
                isLoaded: false,
                items: [],
                couterMorItems: 0,
                hasError: false,
                program: null
              });
            }
              , timeAnimation);
          }
        }

        this.closeMenu();



        //  window.location.href = window.location.href + "#1";
      }
    }
  }

  componentWillMount() {

    window.closeOrBack = () => {
      document.getElementById("backInHeader").click();
    }

    //---נעילת מסך
    window.screen.orientation.lock('portrait');

    //--כתור חזרה לאנדרואיד
    document.addEventListener("backbutton"
      , (e) => {
        window.closeOrBack();


        //  alert("Android back button"+JSON.stringify(e));
        // navigator.app.exitApp();
      }
      , false);

  }


  render() {
    const fthis = this;
    let full_Height = window.innerHeight;
    let header_Height = 60;
    let bottom_Height = this.state.playerOpen ? 84 : 0;

    let body_Height = full_Height - (header_Height + bottom_Height);

    let nowPage = this.state.historyPages[this.state.historyPages.length - 1];
    return (
      [<div id="backInHeader" key="backInHeader"
        onClick={() => {
          fthis.ChangePage(this.state.historyPages[this.state.historyPages.length - 2]
            , this.state.historyPages[this.state.historyPages.length - 1])
        }}></div>

        , <div key="body" className="noScroll body" style={{ width: window.innerWidth, height: full_Height + "px" }}>
        {[
          /* //--header--// */
          <div key="top_section" className="noScroll top_section" style={{ height: header_Height + "px" }}>
            <div className="noScroll navbar w-nav">
              <CheeseburgerMenu
                topOffset={header_Height}
                bottomOffset={bottom_Height + 0.5}
                right={true}
                width={230}
                isOpen={this.state.menuOpen}
                closeCallback={this.closeMenu.bind(this)}>
                <MenuContent myParent={this} closeCallback={this.closeMenu.bind(this)} />
              </CheeseburgerMenu>
              <div style={{ height: header_Height }} className="hamburger">
                <HamburgerMenu
                  isOpen={this.state.menuOpen}
                  menuClicked={
                    this.state.menuOpen
                      ? this.closeMenu.bind(this)
                      : this.openMenu.bind(this)}
                  width={27}
                  height={20}
                  strokeWidth={2.7}
                  rotate={0}
                  color='#31b1ca'
                  borderRadius={0}
                  animationDuration={0.5}
                />
              </div>
            </div>
            <div onClick={() => { window.location.reload(); }} className="top_logo w-inline-block">
              {/* fthis.ChangePage('home', null); fthis.setState({ historyPages: ["home"] });  */}
              <img src="images/logo_radio_turkiz.svg" alt="" /></div>
            <div onClick={() => this.ChangePage("livePlay", null)} className="top_live_button w-inline-block">
              <div>לשידור החי</div>
            </div>
          </div>


          //* //--body--// */
          , <div key="body_div">
            {/* //--home page--// */}
            <div key="home" id="home" className="showPage scrollPage" style={{ height: body_Height, top: header_Height }}>
              {
                nowPage === "home" || fthis.state.historyPages.includes("home")
                  ? <Home myParent={this} />
                  : <div></div>
              }
            </div>

            {/* //--schedule--// */}
            <div key="broadcastSchedule" id="broadcastSchedule" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "broadcastSchedule" || fthis.state.historyPages.includes("broadcastSchedule")
                  ? <BroadcastSchedule myParent={this} />
                  : <div></div>
              }
            </div>

            {/* itemsByCategory */}
            <div key="itemsByCategory" id="itemsByCategory" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "itemsByCategory" || fthis.state.historyPages.includes("itemsByCategory")
                  ? <ItemsByCategory myParent={this} />
                  : <div></div>
              }
            </div>
            {/* livePlay */}
            <div key="livePlay" id="livePlay" style={{ backgroundColor: "#f4f4f4", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "livePlay" || fthis.state.historyPages.includes("livePlay")
                  ? <LivePlay myParent={this} />
                  : <div></div>
              }
            </div>
            {/* Programs */}
            <div key="programs" id="programs" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                (nowPage === "programs" || fthis.state.historyPages.includes("programs")) &&
                <Programs myParent={this} />
              }
            </div>

            {/* category */}
            <div key="category" id="category" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
              <Category myParent={this} />
            </div>

            {/* program */}
            <div key="program" id="program" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
              <Program myParent={this} />
            </div>

          </div>


          //* //--footer player--// */
          ,
          <div key="player_section" className="player_section" style={{ height: bottom_Height + "px", display: this.state.playerOpen ? "flex" : "none" }}>
            <div className="player_info_group">
              <div>{fthis.state.playerItem !== null
                ? fthis.state.playerItem.show_name
                : ""}</div>
              <div className="player_seeking_bar">
                <div className="player_seeking_line" style={{
                  width: fthis.state.playerItem !== null
                    ? (fthis.state.playerTime / (
                      (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                      + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
                        + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
                    ) * 100).toString() + "%"
                    : 0
                }} />
                <div className="player_seeking_bul" style={{
                  marginLeft: fthis.state.playerItem !== null
                    ? (fthis.state.playerTime / (
                      (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                      + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
                        + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
                    ) * 100).toString() + "%"
                    : 0
                }} />
              </div>
              <div>11:00 - 12:00</div>
            </div>
            <div onClick={() => this.playPause()}
              className={fthis.state.playerPlay ? "player_button pause w-inline-block" : "player_button w-inline-block"}>
              <img src={fthis.state.playerPlay ? "./images/ic_pause_blue.svg" : "./images/ic_play_blue.svg"}
                alt={fthis.state.playerPlay ? "puse Player" : "play Player"} />
            </div>
            {/* <div className="player_video_icon"><img src="images/video.svg" alt="" /></div> */}
          </div>

        ]}</div>
      ]
    );
  }
}





