import React from 'react';
import $ from 'jquery';
import CheeseburgerMenu from 'cheeseburger-menu';
import HamburgerMenu from 'react-hamburger-menu';
import MenuContent from './menuContent';

import Home from "./pages/home";
import Schedule from './pages/schedule';

//-----styles----//
import "./css/style.css";
import './css/animate.css';

//----images---//
import logo_blue from './assets/images/logo-blue.svg';


class App extends React.Component {



  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , historyPage: ""
      , screenPages: []
      , historyPages: ["home"]
      , menuOpen: false
    }

    this.listLevelPages = [];
    this.listLevelPages["home"] = 0;
    this.listLevelPages["schedule"] = 1;

    this.ChangePage = this.ChangePage.bind(this);
  }

  openMenu() {
    this.setState({ menuOpen: true })
  }
  closeMenu() {
    this.setState({ menuOpen: false })
  }



  async ChangePage(goToPage, fromPage) {
    //--זמן האנימציה
    const timeAnimation = 250;//ms


    if (goToPage !== fromPage) {
        //---ניהול חזרות----//
        this.bezy = true;
        //סיום האפליקציה, סגור
        if (this.state.historyPages.length === 1
            && goToPage === undefined) {
            window.navigator.app.exitApp();
            this.bezy = false;
        } else {
            ///שמור היסטוריה
            let new_historyPages = this.state.historyPages;

            if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage]) {
                //נכנסים פנימה, הוסף להסטוריה
                new_historyPages.push(goToPage);
            } else {
                //חוזרים אחורה, מחק את הדף האחרון מההיסטוריה.
                new_historyPages.splice(new_historyPages.length - 1, 1);
            }
            //שמירת שינויים בהיסטוריה
            this.setState({
                historyPages: new_historyPages
            });
        }

        //----navigator and animation----///


        if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage]) {
            //--נכנסים דף פנימה Up--//
            if (this.listLevelPages[goToPage] === 1) {
                //Up from level 0 to level 1

                $('#' + goToPage).css('animation', 'slideInRight ' + timeAnimation + 'ms');

                $('#' + goToPage).removeClass('hiddenPage');
                $('#' + goToPage).addClass('scrollPage');
                $('#' + goToPage).addClass('showPage');


                setTimeout(() => {
                    $('#' + goToPage).css('animation', '');

                    $('#' + fromPage).removeClass('showPage');
                    $('#' + fromPage).removeClass('scrollPage');
                    $('#' + fromPage).addClass('hiddenPage');
                    this.bezy = false;
                }, timeAnimation);

            } else if (this.listLevelPages[goToPage] === 2) {
                //Up from level 1 to level 2
                $('#' + goToPage).css('animation', 'zoomIn ' + timeAnimation + 'ms');
                $('#' + fromPage).css('z-index', 0);
                $('#' + goToPage).css('z-index', 89);

                $('#' + goToPage).removeClass('hiddenPage');
                $('#' + goToPage).addClass('scrollPage');
                $('#' + goToPage).addClass('showPage');


                setTimeout(() => {
                    $('#' + goToPage).css('animation', '');
                    $('#' + goToPage).css('z-index', "");
                    $('#' + fromPage).css('z-index', "");

                    $('#' + fromPage).removeClass('showPage');
                    $('#' + fromPage).removeClass('scrollPage');
                    $('#' + fromPage).addClass('hiddenPage');
                    this.bezy = false;
                }, timeAnimation);

            }
        } else {
            //--חזרה בדפים Down--//

            if (this.listLevelPages[goToPage] === 1) {
                //Down from level 2 to level 1
                $('#' + goToPage).css('z-index', 0);

                $('#' + goToPage).removeClass('hiddenPage');
                $('#' + goToPage).addClass('scrollPage');
                $('#' + goToPage).addClass('showPage');

                $('#' + fromPage).css('z-index', 89);
                $('#' + fromPage).css('animation', 'zoomOut ' + timeAnimation + 'ms');
                $('#' + fromPage).removeClass('showPage');
                setTimeout(() => {
                    $('#' + fromPage).addClass('hiddenPage');
                    $('#' + fromPage).css('animation', '');
                    $('#' + goToPage).css('z-index', "");
                    $('#' + fromPage).css('z-index', "");

                    $('#' + fromPage).removeClass('showPage');
                    $('#' + fromPage).removeClass('scrollPage');
                    $('#' + fromPage).addClass('hiddenPage');
                    this.bezy = false;
                }, timeAnimation);

            } else if (this.listLevelPages[goToPage] === 0) {
                //Down from level 1 to level 0
                $('#' + goToPage).css('z-index', 1);

                $('#' + goToPage).removeClass('hiddenPage');
                $('#' + goToPage).addClass('scrollPage');
                $('#' + goToPage).addClass('showPage');
                //   });

                $('#' + fromPage).css('z-index', 89);

                setTimeout(() => {

                    $('#' + fromPage).removeClass('showPage');
                    $('#' + fromPage).removeClass('scrollPage');
                    $('#' + fromPage).addClass('hiddenPage');

                    $('#' + fromPage).css('animation', '');
                    $('#' + goToPage).css('z-index', "");
                    $('#' + fromPage).css('z-index', "");
                    this.bezy = false;
                }, timeAnimation);

                $('#' + fromPage).css('animation', 'slideOutRight ' + timeAnimation + 'ms');

            }
        }
        // //עיצוב כפתור חזרה
        // if (goToPage === "home") {
        //     //  $('#backInHeader').ready(() => {
        //     $('#backInHeader').css('display', "none");
        //     // });
        // } else {
        //     //   $('#backInHeader').ready(() => {
        //     $('#backInHeader').css('display', "flex");
        //     //   });
        // }

    }
}
  componentWillMount() {



    window.closeOrBack = () => {
      document.getElementById("backInHeader").click();
    }

    document.addEventListener("backbutton"
      , (e) => {
        window.closeOrBack();


        //  alert("Android back button"+JSON.stringify(e));
        // navigator.app.exitApp();
      }
      , false);

  }


  render() {
    const fthis = this;

    let full_Height = window.innerHeight;
    let header_Height = 85;

    let body_Height = full_Height - header_Height;

    let nowPage= this.state.historyPages[this.state.historyPages.length - 1];
    return (
      [<div id="backInHeader"
        onClick={() => {
          fthis.ChangePage(this.state.fromPage, this.state.toPage)
        }}></div>

        , <div className="body" style={{ width: window.innerWidth, height: full_Height }}>
        {[
          /* //--header--// */
          <div className="header" style={{ height: header_Height }}>
            <div id="w-node-1780bf9f7c76-4d731c10"><div href="#" className="button w-hidden-main w-button">לשידור החי</div></div>
            <div id="w-node-c52778fc92bb-4d731c10">
              <div className="logo-img-div"><img src={logo_blue} alt="" className="logo-img w-hidden-main" /></div>
            </div>
             <CheeseburgerMenu
              topOffset={header_Height}
              right={true}
              isOpen={this.state.menuOpen}
              closeCallback={this.closeMenu.bind(this)}>
              <MenuContent myParent={this} closeCallback={this.closeMenu.bind(this)} />
            </CheeseburgerMenu>
            <div className="hamburger">
              <HamburgerMenu
                isOpen={this.state.menuOpen}
                menuClicked={
                  this.state.menuOpen
                  ?this.closeMenu.bind(this)
                  :this.openMenu.bind(this)}
                width={32}
                height={18}
                strokeWidth={2}
                rotate={0}
                color='black'
                borderRadius={0}
                animationDuration={0.5}
              />
            </div>
          </div>

          //* //--body--// */
          , <div>
            {/* //--home page--// */}
            <div id="home" style={{ height: body_Height }} className="showPage scrollPage">{
                nowPage === "home"
                  ? <Home myParent={this} />
                  : <div></div>
              }
            </div>

            {/* //--schedule--// */}
            <div id="schedule" style={{ height: body_Height }} className="hiddenPage">{
                nowPage === "schedule"
                  ? <Schedule myParent={this} />
                  : <div></div>
              }
            </div>
          </div>
        ]}</div>
      ]
    );
  }
}

export default App;
