import React from 'react';
import P1 from "./pages/p1";
import P2 from "./pages/p2";
import P3 from "./pages/p3";

//-----styles----//
import "./css/style.css"


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , historyPage: ""
      , focusOnPage: ""
      , pagesComponents: []
    }
  }
  componentWillMount() {

    const list_pagesComponents = [];
    list_pagesComponents["p1"] = P1.prototype.render();//constructor({myParent:this});
    list_pagesComponents["p2"] = P2.prototype.render();
    // P2.constructor({myParent:this});
    list_pagesComponents["p3"] = P3.prototype.render();

    this.setState({
      focusOnPage: "p1"
      , pagesComponents: list_pagesComponents
    });

    // const list_myChildrens = [];
    // list_myChildrens["p1"] = P1//constructor({myParent:this});
    // debugger
    // list_myChildrens["p1"].prototype.setState({myParent:this})
    // list_myChildrens["p1"].constructor(this);
    // list_myChildrens["p2"] = P2;
    // // P2.constructor({myParent:this});
    // list_myChildrens["p3"] = P3;

    this.setState({
      focusOnPage: "p1"
      , pagesComponents: list_pagesComponents
    });
  }


  render() {
    // const PageOnline = this.state.pagesComponents[this.state.focusOnPage];
    return (
      this.state.pagesComponents[this.state.focusOnPage]
      // <PageOnline myParent={this} />
    );
  }
}

export default App;
