import React from 'react';
import $ from 'jquery';
import Error from '../error'

export default class Category extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            myChildrens: false

            , error: false,
            isLoaded: false,
            items: []
            , hasError: false
            , categoryID: 0
            , categoryTitle: ""
        }

        this.name = "category";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["category"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }


    componentDidCatch(error, info) {
        console.log(
            'class component error at:RecomendItems(home.js). '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    play(categoryID, categoryTitle) {

        const fthis = this;

        fthis.setState({
            categoryID: categoryID
            , categoryTitle: categoryTitle
        });

        $.ajax({
            url: "https://api.eol.co.il/radio/v1/categories/" + categoryID,
            type: 'GET',
            dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (results) {
                debugger;
                fthis.setState({
                    isLoaded: true,
                    items: results
                });
            },
            error: function (req, status) {//
                //    alert("error ajax"+ req+status)
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }


    render() {

        const { error, isLoaded, items, hasError, myParent,categoryTitle } = this.state;

        if (hasError) {
            return (
                <Error showRecomends={false} />
            );
        } else {
            if (error) {
                return <Error title={"תקלת תקשורת"} showRecomends={false} />;
            } else if (!isLoaded) {

                return (
                    <div>
                        טוען
              </div>);
            } else {
                return (

                    <div className="content_section w-clearfix">
                        <div className="top_title_group category">
                            <div className="top_title_txt category">{categoryTitle}</div>
                            <div className="cat_link_icon list"><img src="images/cat_ic_spirit.svg" alt="" /></div>
                        </div>
                        <div className="item_group w-clearfix">
                            <div className="item_pict_group"><a href="false" className="item_pict_link w-inline-block" />
                                <div className="item_pict_infogroup">
                                    <h3>האוניברסיטה הקוסמית - ערכים כמפתחות לשינוי</h3>
                                    <div className="item_cat_group"><a href="false" className="item_cat_link">אחת ששומעת</a>
                                        <div className="item_cat_bullet" />
                                        <div>אליענה בן-דוד</div>
                                    </div>
                                </div>
                            </div>
                            <div className="item_date_group">
                                <div>30.01.19</div>
                                <div className="item_time_group">
                                    <div className="item_time">40</div>
                                    <div>דקות</div>
                                </div>
                            </div>
                            <div className="item_txt">כמו שיעור יוגה, שמתחיל במדיטציה, בישיבה עם עיניים עצומות, כדי להרגע, לנשום, לחזור לעצמנו - כך בדיוק התוכנית מתחילה הפעם ומתוך המקום הממורכז, שלו.</div><a href="false" className="item_play_link">להאזנה</a>
                            <div className="item_social_group"><a href="false" className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></a>
                                <div className="social_line" /><a href="false" className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></a>
                                <div className="social_line" /><a href="false" className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></a></div>
                        </div>
