import React from 'react';
import $ from 'jquery';
import Error from '../error'

export default class home extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: false
    }

    this.name = "home";
    this.myApp = this.props.myParent;
    this.myParent = this.props.myParent;

    let new_myChildrens = this.myParent.state.myChildrens;
    new_myChildrens["home"] = this;
    this.myParent.setState({
      myChildrens: new_myChildrens
    });

  }
  //https://api.eol.co.il/radio/v1/categories


  componentWillMount() {

  }

  render() {
    const fthis = this;
    return (
      <div>
        <div className="slider_section">
          <div data-delay={4000} data-animation="slide" data-autoplay={1} data-duration={500} data-infinite={1} className="slider w-slider">
            <div className="w-slider-mask">
              <div className="slide w-slide">
                <div className="slide_content">
                  <h1 className="slider_title">יום מעשים טובים</h1>
                  <h1>כבר כאן</h1>
                  <a href="false" className="slider_button w-inline-block">
                    <div>להאזנה לשידור המיוחד</div>
                  </a>
                </div>
              </div>
              <div className="slide w-slide">
                <div className="slide_content">
                  <h1 className="slider_title">רדין מהות החיים</h1>
                  <h1>יש אלטרנטיבה</h1>
                </div>
              </div>
            </div>
            <div className="w-hidden-main w-hidden-medium w-hidden-small w-hidden-tiny w-slider-arrow-left">
              <div className="w-icon-slider-left" />
            </div>
            <div className="w-hidden-main w-hidden-medium w-hidden-small w-hidden-tiny w-slider-arrow-right">
              <div className="w-icon-slider-right" />
            </div>
            <div className="w-hidden-main w-hidden-medium w-hidden-small w-hidden-tiny w-slider-nav w-round" />
          </div>
        </div>
        <div className="search_section">
          <div className="search_form w-form">
            <form className="w-clearfix"><a href="false" className="search_link w-inline-block"><img src="images/ic_search.svg" alt="" /></a><input type="email" maxLength={256} required placeholder="מה תרצו לשמוע?" className="search_field w-input" /></form>
            <div className="w-form-done">
              <div>Thank you! Your submission has been received!</div>
            </div>
            <div className="w-form-fail">
              <div>Oops! Something went wrong while submitting the form.</div>
            </div>
          </div>
        </div>
        <div className="content_section grey">
          <h1 className="h_slogan">האונדימנד שלנו. כל התכניות. בזמן שלך.</h1>
          <div className="h_slogan_line" />
          <div>רוצים להכיר את התוכן שלנו מקרוב?<br />סידרנו אותו בכמה קטגוריות שאולי יעניינו אותך.</div>
          <Categories myParent={fthis} />
        </div>
        <RecomendItems myParent={fthis} />
      </div>

    );
  }

}




 class Categories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      isLoaded: false,
      items: []
      , hasError: false
    };
    this.myParent = this.props.myParent;
  }
  componentDidCatch(error, info) {
    console.log(
      'class component error at:RecomendItems(home.js). '
      , new Error()
      , 'error message:', JSON.stringify(error)
      , 'info message:', JSON.stringify(info));
    this.setState({ hasError: true });
    // logErrorToMyService(error, info);
  }


  componentDidMount() {
    const fthis = this;
    $.ajax({
      url: "https://api.eol.co.il/radio/v1/categories",
      type: 'GET',
      dataType: "json",
      contentType: 'application/json; charset = utf-8',
      success: function (results) {
        fthis.setState({
          isLoaded: true,
          items: results
        });
      },
      error: function (req, status) {//
        //    alert("error ajax"+ req+status)
        console.log(req, status);
        fthis.setState({
          isLoaded: true,
          error: true
        });
      }
    });
  }

  render() {

    const { error, isLoaded, items, hasError } = this.state;

    if (hasError) {
      return (
        <div>אופס, הייתה תקלה</div>
      );
    } else {
      if (error) {
        return <div><h4>תקלת תקשורת</h4></div>;
      } else if (!isLoaded) {

        return (
          <div>טוען
          </div>);
      } else {
        return (
          <div className="cat_nav_group">
            {items.map((item) =>
              <div id={item.category_id} key={item.category_id} className="cat_nav_link w-inline-block">
                <div className="cat_link_icon"><img src={item.category_image_link} alt={item.category_title} /></div>
                <div>{item.category_title}</div>
              </div>)}
          </div>
        )
      }

    }
  }
}







 class RecomendItems extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      isLoaded: false,
      items: []
      , hasError: false
      , myParent: this.props.myParent
    };
  }
  componentDidCatch(error, info) {
    console.log(
      'class component error at:RecomendItems(home.js). '
      , new Error()
      , 'error message:', JSON.stringify(error)
      , 'info message:', JSON.stringify(info));
    this.setState({ hasError: true });
    // logErrorToMyService(error, info);
  }


  componentDidMount() {
    const fthis = this;
    $.ajax({
      url: "https://api.eol.co.il/radio/v1/hp-recommended",
      type: 'GET',
      dataType: "json",
      contentType: 'application/json; charset = utf-8',
      success: function (results) {
        fthis.setState({
          isLoaded: true,
          items: results
          ,error:true
        });
      },
      error: function (req, status) {//
        //    alert("error ajax"+ req+status)
        console.log(req, status);
        fthis.setState({
          isLoaded: true,
          error: true
        });
      }
    });
  }

  render() {

    const { error, isLoaded, items, hasError, myParent } = this.state;

    if (hasError) {
      return (
        <Error showRecomends={false}/>
      );
    } else {
      if (error) {
        return  <Error title={"תקלת תקשורת"} showRecomends={true}/>;
      } else if (!isLoaded) {

        return (
          <div>
            טוען
          </div>);
      } else {
        return (
          <div className="content_section w-clearfix" style={{ paddingBottom: myParent.myParent.state.playerOpen ? "" : "15px" }}>
            <h2>המומלצים שלנו</h2>
            {
              items.map((item,index) => {
                return <RecomendItem item={item} index={index} />
              })
            }




            <a href="false" className="add_button w-inline-block">
              <div>עוד שידורים</div>
            </a>
          </div>

        );
      }
    }
  }
}

export class RecomendItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false
    }
    this.myParent = this.props.myParent;
  }
  componentDidCatch(error, info) {
    console.log(
      'class component error at: RecomendItem. '
      , new Error()
      , 'error message:', JSON.stringify(error)
      , 'info message:', JSON.stringify(info));
    this.setState({ hasError: true });
    // logErrorToMyService(error, info);
  }


  componentWillMount() {

  }
  render() {
    if (this.state.hasError) {
      return (
        <div>error!!</div>
      );
    } else {
      const {item,index} = this.props;
      return (
        <div key={item.show_id}>
          <div className={index===0?"w-clearfix":"item_group w-clearfix"}>
            <div className="item_pict_group" style={{backgroundImage:'url('+item.show_image+')'}}>
              <div className="item_pict_link w-inline-block"></div>    {/* a */}
              <div className="item_pict_infogroup">
                <h3>{item.show_name}</h3>
                <div className="item_cat_group">
                  <div className="item_cat_link">{item.parent_program_name}</div>
                  <div className="item_cat_bullet" />
                  <div>{item.broadcaster}</div>
                </div>
              </div>
            </div>
            <div className="item_date_group">
              <div>{item.show_pub_date}</div>
              <div className="item_time_group">
                <div className="item_time">
                  {Math.round(parseFloat(item.show_length.substring(3, 5) + "." + item.show_length.substring(6, 9)))}
                </div>
                <div>דקות</div>
              </div>
            </div>
            <div className="item_txt" dangerouslySetInnerHTML={{ __html: item.show_description }}>
            </div><a href="false" className="item_play_link">להאזנה</a>
            <div className="item_social_group"><a href="false" className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></a>
              <div className="social_line" /><a href="false" className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></a>
              <div className="social_line" /><a href="false" className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></a></div>
          </div>

        </div>


        //  <div className="reccom-info-div w-hidden-main">
        //     <div className="reccomandedpic-div recommend">
        //       <div className="shade-div">
        //         <h6 className="heading-on-shade1 w-hidden-main">{this.props.title}</h6>
        //         <h6 className="heading-on-shade2 w-hidden-main">?אחת ששומעת? • אליענה בן דוד</h6>
        //       </div>
        //     </div>
        //     <h6 className="time-heading w-hidden-main">
        //       {this.props.pubDate.getDate()
        //         + "." +
        //         this.props.pubDate.getMonth()
        //         + "." +
        //         this.props.pubDate.getFullYear().toString().substr(2, 3)}
        //       | ?? דקות</h6>
        //     <p className="recommended-paragraph w-hidden-main">כמו שיעור יוגה, שמתחיל במדיטציה, בישיבה עם עיניים עצומות, כדי להרגע, לנשום, לחזור לעצמנו - כך בדיוק התוכנית מתחילה הפעם ומתוך המקום הממרוכז,</p><div href="false" className="listen-link">להאזנה</div>
        //     <div className="recommended-space-div underline2 w-hidden-main" />
        //   </div>
      );
    }
  }
}
