import React from 'react';
import { RecomendItems } from './pages/home'
//import $ from 'jquery';


export default class LivePlay extends React.Component {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         error: null,
    //         isLoaded: false,
    //         items: []
    //         , hasError: false
    //     }
    // }
    render() {
        const { title, code, showRecomends } = this.props;
        return (
            <div>
                <div>
                    <div className="content_section program">
                        <div className="program_title_group _404">
                            <h1 className="program_main_title _404">{title === null ? "שגיאה" : title}</h1>
                            <div>
                                {code === 404 ? { __html: " מחפשים תוכן ולא מוצאים? הכיל טוב :)<br />לפעמים שעמום הוא דבר חיובי." } : ""}
                            </div>
                        </div>
                    </div>
                    {showRecomends &&
                        (<div className="content_section w-clearfix">
                            <div className="top_title_group">
                                <div className="top_title_txt">בינתיים, אולי תרצו להכיר תכנים חדשים ומעניינים?</div>
                            </div>

                            <RecomendItems />

                        </div>)
                    }

                </div>

            </div>);
    }

}
