import React from 'react';
import { RecomendItem } from './pages/home'
import $ from 'jquery';


export default class Error extends React.Component {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         error: null,
    //         isLoaded: false,
    //         items: []
    //         , hasError: false
    //     }
    // }
    render() {
        const { title, code, showRecomends } = this.props;
        return (
            <div>
                <div>
                    <div className="content_section program">
                        <div className="program_title_group _404">
                            <h1 className="program_main_title _404">{title === null ? "שגיאה" : title}</h1>
                            <div>
                                {code === 404 ? { __html: " מחפשים תוכן ולא מוצאים? הכיל טוב :)<br />לפעמים שעמום הוא דבר חיובי." } : ""}
                            </div>
                        </div>
                    </div>
                    {showRecomends &&
                        (<div className="content_section w-clearfix">
                            <div className="top_title_group">
                                <div className="top_title_txt">בינתיים, אולי תרצו להכיר תכנים חדשים ומעניינים?</div>
                            </div>

                            <RecomendItems />

                        </div>)
                    }

                </div>

            </div>);
    }

}




export class RecomendItems extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: false,
        isLoaded: false,
        items: []
        , hasError: false
      };
    }
    componentDidCatch(error, info) {
      console.log(
        'class component error at:RecomendItems(error.js). '
        , new Error()
        , 'error message:', JSON.stringify(error)
        , 'info message:', JSON.stringify(info));
      this.setState({ hasError: true });
      // logErrorToMyService(error, info);
    }
  
  
    componentDidMount() {
      const fthis = this;
      $.ajax({
        url: "https://api.eol.co.il/radio/v1/hp-recommended",
        type: 'GET',
        dataType: "json",
        contentType: 'application/json; charset = utf-8',
        success: function (results) {
          fthis.setState({
            isLoaded: true,
            items: results
          });
        },
        error: function (req, status) {//
          //    alert("error ajax"+ req+status)
          console.log(req, status);
          fthis.setState({
            isLoaded: true,
            error: true
          });
        }
      });
    }
  
    render() {
  
      const { error, isLoaded, items, hasError, myParent } = this.state;
  
      if (hasError) {
        return (
          <Error showRecomends={false}/>
        );
      } else {
        if (error) {
          return  <Error title={"תקלת תקשורת"} showRecomends={false}/>;
        } else if (!isLoaded) {
  
          return (
            <div>
              טוען
            </div>);
        } else {
          return (
            <div className="content_section w-clearfix" style={{ paddingBottom: myParent.myParent.state.playerOpen ? "" : "15px" }}>
              <h2>המומלצים שלנו</h2>
              {
                items.map((item,index) => {
                  return <RecomendItem item={item} index={index} />
                })
              }
  
  
  
  
              <a href="false" className="add_button w-inline-block">
                <div>עוד שידורים</div>
              </a>
            </div>
  
          );
        }
      }
    }
  }
