import React from 'react';
import $ from 'jquery';
import Error from '../error'

export default class Program extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            myChildrens: false
            , error: false,
            isLoaded: false,
            items: []
            , couterMorItems: 0
            , hasError: false
            , program: null
            , ajax: false
            , isFrom_broadcastSchedule: true
            , cash: []
        }

        this.name = "program";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["program"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }


    componentDidCatch(error, info) {
        console.log(
            'class component error at:Program.Program.js ' + this.name
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    play(program, isFrom_broadcastSchedule) {
        //debugger
        const fthis = this;

        if (fthis.state.cash[program.program_id] === undefined) {
            fthis.setState({
                program: program
                , ajax: $.ajax({
                    url: "https://api.eol.co.il/radio/v1/programs/" + program.program_id,
                    type: 'GET',
                    dataType: "json",
                    contentType: 'application/json; charset = utf-8',
                    success: function (results) {
                       // debugger
                        let newCash = fthis.state.cash;
                        newCash[program.program_id] = results;
                        fthis.setState({
                            isLoaded: true,
                            items: results
                            , cash: newCash
                            , isFrom_broadcastSchedule: isFrom_broadcastSchedule
                        });
                    },
                    error: function (req, status) {//
                        //    alert("error ajax"+ req+status)
                        console.log(req, status);
                        fthis.setState({
                            isLoaded: true,
                            error: true
                        });
                    }
                })
            });

        } else {
            fthis.setState({
                program: program,
                isLoaded: true,
                items: fthis.state.cash[program.program_id]
            });
        }
    }


    render() {

        const fthis = this;
        const { isFrom_broadcastSchedule, error, isLoaded, items, hasError, couterMorItems, program } = this.state;

        if (hasError) {
            return (
                <Error myApp={fthis.myApp} showRecomends={false} />
            );
        } else {
            if (error) {
                return <Error myApp={fthis.myApp} title={"תקלת תקשורת"} showRecomends={false} />;
            } else if (!isLoaded && program !== null) {
                return (
                    <div>
                        <div className="content_section program">
                            <div className="list_top_picture"><img
                                src={isFrom_broadcastSchedule ? program.image_url : program.program_image}
                                alt={isFrom_broadcastSchedule ? program.name : program.program_name} />
                            </div>
                            <div className="program_title_group">
                                <h1 className="program_main_title">{isFrom_broadcastSchedule ? program.name : program.program_name}</h1>
                                <h1 className="program_main_title">{isFrom_broadcastSchedule ? program.presenter : program.broadcaster}</h1>
                                <div className="program_title_line" />
                                <div>
                                    {isFrom_broadcastSchedule ? program.presenter : program.program_description}
                                </div>
                            </div>
                        </div>
                        <div className="content_section w-clearfix">

                            <div className="item_group w-clearfix" style={{ width: "100%" }}>
                                <div className="item_pict_group loadingBackgrounAnimation">
                                    <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                    <div className="item_pict_infogroup">
                                        <h3 className="loadingBackgrounAnimation"> </h3>
                                        <div className="item_cat_group">
                                            <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                            <div className="item_cat_bullet" />
                                            <div className="loadingBackgrounAnimation"> </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="item_date_group">
                                    <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                    <div className="item_time_group">
                                        <div className="item_time loadingBackgrounAnimation">
                                            00
                                    </div>
                                        <div>דקות</div>
                                    </div>
                                </div>
                                <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                                </div><div className="item_play_link">להאזנה</div>
                                <div className="item_social_group">
                                    <div className="share_basic w-inline-block">
                                        <div className="share_basic_icon"><img src="images/ic_share.svg" alt="" /></div>
                                        <div className="share_txt">שתף</div>
                                    </div>
                                </div>     </div>
                            <div className="item_group w-clearfix" style={{ width: "100%" }}>
                                <div className="item_pict_group loadingBackgrounAnimation">
                                    <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                    <div className="item_pict_infogroup">
                                        <h3 className="loadingBackgrounAnimation"> </h3>
                                        <div className="item_cat_group">
                                            <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                            <div className="item_cat_bullet" />
                                            <div className="loadingBackgrounAnimation"> </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="item_date_group">
                                    <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                    <div className="item_time_group">
                                        <div className="item_time loadingBackgrounAnimation">
                                            00
                                    </div>
                                        <div>דקות</div>
                                    </div>
                                </div>
                                <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                                </div><div className="item_play_link">להאזנה</div>
                                <div className="item_social_group">
                                    <div className="share_basic w-inline-block">
                                        <div className="share_basic_icon"><img src="images/ic_share.svg" alt="" /></div>
                                        <div className="share_txt">שתף</div>
                                    </div>
                                </div>         </div>
                            <div className="item_group w-clearfix" style={{ width: "100%" }}>
                                <div className="item_pict_group loadingBackgrounAnimation">
                                    <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                    <div className="item_pict_infogroup">
                                        <h3 className="loadingBackgrounAnimation"> </h3>
                                        <div className="item_cat_group">
                                            <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                            <div className="item_cat_bullet" />
                                            <div className="loadingBackgrounAnimation"> </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="item_date_group">
                                    <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                    <div className="item_time_group">
                                        <div className="item_time loadingBackgrounAnimation">
                                            00
                                    </div>
                                        <div>דקות</div>
                                    </div>
                                </div>
                                <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                                </div><div className="item_play_link">להאזנה</div>
                                <div className="item_social_group">
                                    <div className="share_basic w-inline-block">
                                        <div className="share_basic_icon"><img src="images/ic_share.svg" alt="" /></div>
                                        <div className="share_txt">שתף</div>
                                    </div>
                                </div>     </div>
                        </div>
                    </div>
                );
            } else {

                if (items.length === 0) { return (<Error myApp={fthis.myApp} title="לא נמצאו תוצאות לבקשתך" code={404} showRecomends={true} />) }
                else
                    //debugger
                    return (

                        <div>
                            <div className="content_section program">
                                <div className="list_top_picture"><img
                                    src={isFrom_broadcastSchedule ? program.image_url : program.program_image}
                                    alt={isFrom_broadcastSchedule ? program.name : program.program_name} />
                                </div>
                                <div className="program_title_group">
                                    <h1 className="program_main_title">{isFrom_broadcastSchedule ? program.name : program.program_name}</h1>
                                    <h1 className="program_main_title">{isFrom_broadcastSchedule ? program.presenter : program.broadcaster}</h1>
                                    <div className="program_title_line" />
                                    <div>
                                        {isFrom_broadcastSchedule ? program.presenter : program.program_description}
                                    </div>
                                </div>
                            </div>
                            <div className="content_section w-clearfix" >
                                {/* style={{ paddingBottom: fthis.myParent.state.playerOpen ? "160px" : "75px" }}> */}
                                <h2>הפודקאסטים הפופולארים</h2>

                                {items.slice(0, 5 * couterMorItems + 5).map(item =>
                                    <Item key={item.show_id} item={item} myParent={fthis} myApp={fthis.myApp} />
                                )}

                                {((5 * couterMorItems + 5) <= items.length) &&
                                    <div className="add_button w-inline-block">
                                        <div onClick={() => fthis.setState({ couterMorItems: fthis.state.couterMorItems + 1 })}>עוד שידורים</div>
                                    </div>
                                }
                            </div>
                        </div>
                    );
            }
        }

    }
}



class Item extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            hasError: false
            , shareLoading: false
        }
        this.myParent = this.props.myParent;
        this.myApp = this.props.myApp;
    }
    componentDidCatch(error, info) {
        console.log(
            'class Item error at: category.js '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    componentWillMount() {

    }
    render() {
        const fthis = this;
        const shareLoading = this.state.shareLoading;
        if (this.state.hasError) {
            return (
                <div>error!!</div>
            );
        } else {
            let { item } = this.props;
            item.isLive = false;
            return (
                <div key={item.show_id} className="item_group w-clearfix">
                    <div className="item_pict_group" style={{ backgroundImage: 'url(' + item.show_image + ')' }}>
                        <div className="item_pict_link w-inline-block"></div>    {/* a */}
                        <div className="item_pict_infogroup">
                            <h3>{item.show_name}</h3>
                            {/* <div className="item_cat_group">
                                <div className="item_cat_link">{item.parent_program_name}</div>
                                <div className="item_cat_bullet" />
                                <div>{item.broadcaster}</div>
                            </div> */}
                        </div>
                    </div>
                    <div className="item_date_group">
                        <div>{item.show_pub_date}</div>
                        <div className="item_time_group">
                            <div className="item_time">
                                {Math.round((parseFloat(item.show_length.substring(0, 2)) * 60) + parseFloat(item.show_length.substring(3, 5) + "." + item.show_length.substring(6, 9)))}
                            </div>
                            <div>דקות</div>
                        </div>
                    </div>
                    <div className="item_txt" dangerouslySetInnerHTML={{ __html: item.show_description }}>
                    </div><div onClick={() => fthis.myApp.state.player.playerPlayNew(item)} className="item_play_link">להאזנה</div>
                    <div className="item_social_group">
                        <div className="share_basic w-inline-block">
                            <div className="share_basic_icon" onClick={() => {
                           
                                fthis.setState({ shareLoading: true });
                                window.plugins.socialsharing.shareWithOptions(
                                    {
                                        message: 'רדיו מהות החיים. ' + (item.show_name !== undefined ? item.show_name : "")
                                            + " עם " + (item.broadcaster !== undefined ? item.broadcaster : ""), // not supported on some apps (Facebook, Instagram)
                                        subject: 'רדיו מהות החיים. ' + (item.show_name !== undefined ? item.show_name : "")
                                            + " עם " + (item.broadcaster !== undefined ? item.broadcaster : ""), // fi. for email
                                        // files: [item.show_image], // an array of filenames either locally or remotely
                                        url: item.show_url,
                                        chooserTitle: 'רדיו מהות החיים', // Android only, you can override the default share sheet title,
                                        // appPackageName: '' // Android only, you can provide id of the App you want to share with
                                    },
                                    (result) => {
                                        fthis.setState({ shareLoading: false });
                                        console.log("Share completed? " + result.completed); // On Android apps mostly return false even while it's true
                                        console.log("Shared to app: " + result.app); // On Android result.app since plugin version 5.4.0 this is no longer empty. On iOS it's empty when sharing is cancelled (result.completed=false)
                                    },
                                    (msg) => {
                                        fthis.setState({ shareLoading: false });
                                        console.log("Sharing failed with message: " + msg);
                                    }
                                );
                            }}
                                style={shareLoading ? {
                                    webkitAnimation: 'spin 2s linear infinite'
                                    , animation: 'spin 2s linear infinite'
                                } : {
                                        webkitAnimation: ''
                                        , animation: ''
                                    }}><img src="images/ic_share.svg" alt="" /></div>
                            <div className="share_txt">שתף</div>
                        </div>
                    </div>   </div>
            )
        }
    }
}
