import React from 'react';
//import $ from 'jquery';
import SweetAlert from 'sweetalert2-react';

export default class Swel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showSwal: true
        }
    }
    render() {
        return (
            <div>
                <button style={{width:"100px"}} onClick={() => this.setState({ show: true })}>Alert</button>
                <SweetAlert
                    show={this.state.show}
                    title="Demo"
                    text="SweetAlert in React"
                    onConfirm={() => this.setState({ show: false })}
                />
            </div>
        );
    }
}
