import React from 'react';

import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'


export default class Swel extends React.Component {
    constructor(props) {
        super(props);

        this.name = "swal";
        this.myApp = this.props.myApp;

        let new_myChildrens = this.myApp.state.myChildrens;
        new_myChildrens["swal"] = this;
        this.myApp.setState({
            myChildrens: new_myChildrens
        });


        this.state = {
            //showSwal: true,
            result: {//אף שדה הוא לא שדה חובה (לכולם יש ברירת מחדל)
                important: false//קובע אם המשתמש יוכל לדלג ולהמשך או באפליקציה או שיהיה חייב לעדכן גירסה
                , title: "title"
                , text: "text"
                , html: `<h1 style="color:red;">innerHTML</h1>`
                , type: "warning"//"warning","info","question"////,"error","success"
                , showCancelButton: true
                , showConfirmButton: true
                , showButtonRemindMeLater: true
                , confirmButtonText: "confirmButtonText"
                , linkOnClickConfirmButtom: "https://www.eol.co.il/"
                , cancelButtonText: "confirmButtonText"
                , confirmButtonColor: "blue"//default=#31b1ca
                , cancelButtonColor: "#aaa"//default="#aaa"
                //, padding: ""
                // , background: ""

            }
        }
        this.MySwal = withReactContent(Swal);

    }
    runSwal(whenCloseApp) {
        const fthis = this;
        const result = this.state.result;
        this.MySwal.fire({
            html: result.html
            , title: result.title
            , text: result.text
            , type: result.type
            , showCancelButton: result.showCancelButton && !whenCloseApp
            , showConfirmButton: result.showConfirmButton
            , showCloseButton: result.showCloseButton
            , confirmButtonText: result.confirmButtonText
            , cancelButtonText: result.cancelButtonText
            , confirmButtonColor: result.confirmButtonColor !== undefined ? result.confirmButtonColor : '#31b1ca'
            , cancelButtonColor: result.cancelButtonColor !== undefined ? result.cancelButtonColor : '#aaa'
            , footer:
                !whenCloseApp && result.showButtonRemindMeLater
                    ? <div onClick={() => { fthis.myApp.setState({ showSwalLater: true }); this.MySwal.clickCancel() }}>הזכר לי מאוחר יותר</div>
                    : ``
            , padding: result.padding
            , background: result.background
            , show: this.state.showSwal
            //, footer: 'Copyright 2018'
            , onOpen: () => {
                // `MySwal` is a subclass of `Swal`
                //   with all the same instance & static methods
                // MySwal.clickConfirm()
            }
        }).then((click) => {
            if (click.dismiss !== "cancel") {
                window.open(result.linkOnClickConfirmButtom);
            }
            if (result.important) return fthis.runSwal();
        })
    }
    componentWillMount() {
        this.runSwal();
    }
    render() {
        return null;
    }
}













