import React from 'react';
import $ from 'jquery';

export default class BroadcastSchedule extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            todayIndex: new Date().getDay()
            , sliderDayIndex: new Date().getDay()
            , todayDate: new Date()
            , sliderDate: new Date()
            , maxDaysMoveToSlider: 7
            , sliderDestansToday: 0
        }
        this.weekday = ["יום ראשון", "יום שני", "יום שלישי", "יום רביעי", "יום חמישי", "יום שישי", "יום שבת"];
        this.name = "broadcastSchedule";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["broadcastSchedule"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }

    // componentDidMount() {

    // }


    // componentWillMount() {

    // }

    render() {
        let fthis = this;
        return (
            <div>

                <div className="content_section guide">
                    <h1 className="list_title">כל התכניות</h1>
                    <div className="guide_calendar_group">
                        <div onClick={() => {
                            if (fthis.state.sliderDestansToday > fthis.state.maxDaysMoveToSlider * -1)
                                fthis.setState({
                                    sliderDayIndex: fthis.state.sliderDayIndex === 0 ? 6 : fthis.state.sliderDayIndex - 1
                                    , sliderDate: new Date(new Date().setDate(fthis.state.sliderDate.getDate() - 1))
                                    , sliderDestansToday: fthis.state.sliderDestansToday - 1
                                });
                        }} className="calendar_arrow w-inline-block">
                            <div className="w-icon-slider-right" />
                        </div>
                        <div className="calendar_day">
                            {this.state.todayDate.getDate() === this.state.sliderDate.getDate() ? "היום" : this.weekday[this.state.sliderDayIndex]}
                        </div>
                        <div>{window.dateFormat(fthis.state.sliderDate, "dd.mm.yy")}</div>
                        <div onClick={() => {
                            if (fthis.state.sliderDestansToday < fthis.state.maxDaysMoveToSlider)
                                fthis.setState({
                                    sliderDayIndex: fthis.state.sliderDayIndex === 6 ? 0 : fthis.state.sliderDayIndex + 1
                                    , sliderDate: new Date(new Date().setDate(fthis.state.sliderDate.getDate() + 1))
                                    , sliderDestansToday: fthis.state.sliderDestansToday + 1
                                });
                        }} className="calendar_arrow w-inline-block">
                            <div className="w-icon-slider-left" />
                        </div>
                    </div>

                    <ListBroadcastScheduleDay myParent={fthis} date="" />

                    <a href="false" className="add_button guide w-inline-block">
                        <div>הצג עוד</div>
                    </a>
                </div>


            </div>
        );
    }
}





class ListBroadcastScheduleDay extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            date: this.props.date,
            error: null,
            isLoaded: false,
            items: [],
            myParent: this.props.myParent
        }
    }

    componentDidMount() {

        const fthis = this;
        $.ajax({
            url: 'https://api.eol.co.il/radio/v1/today-schedule?startDate='
                + (window.dateFormat(new Date(new Date().setDate(new Date().getDate() - fthis.state.myParent.state.maxDaysMoveToSlider)), 'yyyy-mm-dd'))
                + '&endDate='
                + (window.dateFormat(new Date(new Date().setDate(new Date().getDate() + fthis.state.myParent.state.maxDaysMoveToSlider)), 'yyyy-mm-dd')),
            type: 'GET',
            // async: false,
            // data: '',
            dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (results) {
                results.forEach(item => {
                    item.isLiveNow =
                        new Date(item.start) <= new Date() && (new Date(new Date().setTime(new Date(item.start).getTime()
                            + (item.duration * 60 * 1000)))) >= new Date();
                });

                fthis.setState({
                    isLoaded: true,
                    items: results
                });
            },
            error: function (req, status) {//
                debugger;
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }


    componentWillMount() {

    }

    render() {

        const { error, isLoaded, items } = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;

        } else if (!isLoaded) {
            return <div>
                <div className="guide_item w-inline-block">
                    <div className="guide_item_plate" />
                    <div className="guide_title_group">
                        <div className="guide_item_date loadingBackgrounAnimation" style={{ width: "80%", height: "39px" }}></div>

                    </div>
                    <div className="loadingBackgrounAnimation" style={{ width: "90%", height: "26px" }}></div>
                </div>
                <div className="guide_item w-inline-block">
                    <div className="guide_item_plate" />
                    <div className="guide_title_group">
                        <div className="guide_item_date loadingBackgrounAnimation" style={{ width: "80%", height: "39px" }}></div>

                    </div>
                    <div className="loadingBackgrounAnimation" style={{ width: "90%", height: "26px" }}></div>
                </div>
                <div className="guide_item w-inline-block">
                    <div className="guide_item_plate" />
                    <div className="guide_title_group">
                        <div className="guide_item_date loadingBackgrounAnimation" style={{ width: "80%", height: "39px" }}></div>

                    </div>
                    <div className="loadingBackgrounAnimation" style={{ width: "90%", height: "26px" }}></div>
                </div>
                <div className="guide_item w-inline-block">
                    <div className="guide_item_plate" />
                    <div className="guide_title_group">
                        <div className="guide_item_date loadingBackgrounAnimation" style={{ width: "80%", height: "39px" }}></div>

                    </div>
                    <div className="loadingBackgrounAnimation" style={{ width: "90%", height: "26px" }}></div>
                </div>
            </div>
                ;

        } else {

            return (
                items.map(item => (
                    <a href="false" className="guide_item w-inline-block">
                        <div className={item.isLiveNow ? "guide_item_plate live" : "guide_item_plate"} />
                        <div className="guide_title_group">
                            <div className="guide_item_date">
                                {((new Date(item.start)).toLocaleTimeString()).substr(0, ((new Date(item.start)).toLocaleTimeString().lastIndexOf(":")))}
                                -
                            {((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                                    .substr(0,
                                        (((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                                            .lastIndexOf(":")))} </div>
                            <div className="guide_item_name">{item.name}</div>
                        </div>
                        <div>{item.description}</div>
                    </a>
                ))
            );
        }
    }
}
