import React from 'react';
//import $ from 'jquery';
import Home from "./pages/home";
import P2 from "./pages/p2";

//-----styles----//
import "./css/style.css";
import './css/animate.css';

//----images---//
import logo_blue from './assets/images/logo-blue.svg';
import menu from './assets/images/menu.svg';


class App extends React.Component {

  pagesComponents = [];

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , historyPage: ""
      , screenPages: []
      , screenPageNow: "p1"
    }

    this.ChangePage = this.ChangePage.bind(this);
  }


  ChangePage(goToPage) {
    // if (goToPage !== "home"){
    //     $('#backInHeader').ready(() => {
    //         $('#backInHeader').css('opacity', 1);
    //     });
    // }else{
    //     $('#backInHeader').ready(() => {
    //         $('#backInHeader').css('opacity', 0);
    //     });
    // }
    this.state.myChildrens.forEach((page) => {
      page.setState({ hidden: page.state.name !== goToPage });
    });
    // }
    this.setState({ historyPage: goToPage });
    window.closeOrBack_btnId = 'closeOrBack_from-' + goToPage;
    window.closeOrBack = () => {
      document.getElementById(window.closeOrBack_btnId).click();
    }
  }
  componentWillMount() {
  }


  render() {

    let full_Height = window.innerHeight;
    let header_Height = 85;

    let body_Height = full_Height - header_Height;

    return (
      [<button className="hiddenPage" id="closeOrBack_from-home" onClick={() => window.navigator.app.exitApp()}></button>

        , <div className="body" style={{ width: window.innerWidth, height: full_Height }}>
        {[
        /* //--header--// */
        <div className="header" style={{ height: header_Height }}>
          <div id="w-node-1780bf9f7c76-4d731c10"><div href="#" className="button w-hidden-main w-button">לשידור החי</div></div>
          <div id="w-node-c52778fc92bb-4d731c10">
            <div className="logo-img-div"><img src={logo_blue} alt="" className="logo-img w-hidden-main" /></div>
          </div>
          <div id="w-node-0f070c45dc9b-4d731c10" className="logo-img-div">
            <div className="humborger-div w-hidden-main"><img src={menu} alt="" className="image-4" /></div>
          </div>
        </div>
        /* //--home page--// */
        , <Home height={body_Height} myParent={this} hidden={false} />
        /* //--p2--// */
        , <P2 myParent={this} hidden={true} />
        ]}</div>
      ]
    );
  }
}

export default App;
