import React from 'react';

import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'


export default class Swel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //showSwal: true,
             result: {
                important: true//קובע אם המשתמש יוכל לדלג ולהמשך או באפליקציה או שיהיה חייב לעדכן גירסה
                , title: "title"
                , text: "text"
                , html: `<h1 style="color:red;">innerHTML</h1>`
                , type: "warning"//"warning","info","question"////,"error","success"
                , showCancelButton: true
                , showConfirmButton: true
                , showButtonRemindMeLater: true
                , confirmButtonText: "confirmButtonText"
                , linkOnClickConfirmButtom: "https://www.eol.co.il/"
                , cancelButtonText: "confirmButtonText"
                , confirmButtonColor: "blue"
                , cancelButtonColor: "#aaa"


                //, padding: ""
                // , background: ""

            }
        }
        const MySwal = withReactContent(Swal);
        MySwal.fire({
            title: <p>Hello World</p>,
            footer: 'Copyright 2018',
            onOpen: () => {
                // `MySwal` is a subclass of `Swal`
                //   with all the same instance & static methods
                MySwal.clickConfirm()
            }
        }).then(() => {
            return MySwal.fire(<p>Shorthand works too</p>)
        })
    }
    render() {
        return <div></div>
    }
}













