import React from 'react';
import $ from 'jquery';
import CheeseburgerMenu from 'cheeseburger-menu';
import HamburgerMenu from 'react-hamburger-menu';
import MenuContent from './menuContent';

import Home from "./pages/home";
import P2 from "./pages/p2";

//-----styles----//
import "./css/style.css";
import './css/animate.css';

//----images---//
import logo_blue from './assets/images/logo-blue.svg';
import menu from './assets/images/menu.svg';


class App extends React.Component {



  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , historyPage: ""
      , screenPages: []
      , historyPages: ["home"]
    }

    this.listLevelPages = [];
    this.listLevelPages["home"] = 0;

    this.ChangePage = this.ChangePage.bind(this);
  }



  ChangePage(goToPage, fromPage) {

    //ניהול חזרות
    if (this.state.historyPages.length === 0)
      window.navigator.app.exitApp();
    else {
      let new_historyPages = this.state.historyPages;

      if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage])
        new_historyPages.push(goToPage);
      else
        new_historyPages.splice(new_historyPages.length - 1, 1);

      this.setState({
        historyPages: new_historyPages
      });
    }
    //עיצוב כפתור חזרה
    if (goToPage === "home") {
      $('#backInHeader').ready(() => {
        $('#backInHeader').css('disply', "none");
      });
    } else {
      $('#backInHeader').ready(() => {
        $('#backInHeader').css('disply', "flex");
      });
    }

    //----navigator and animation--///

    const timeAnimation = 300;//ms
    if (goToPage !== fromPage) {

      if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage]) {//נכנסים דף פנימה Up
        if (this.listLevelPages[goToPage] === 1) {//Up from level 0 to level 1

          $('#' + goToPage).css('animation', 'slideInLeft ' + timeAnimation + 'ms');

          this.state.myChildrens[goToPage].setState({ hidden: false });

          setTimeout(() => {
            $('#' + goToPage).css('animation', '');
            this.state.myChildrens[fromPage].setState({ hidden: true });
          }, timeAnimation);
        }
      } else if (this.listLevelPages[goToPage] === 2) {//Up from level 1 to level 2

        $('#' + goToPage).css('animation', 'zoomIn ' + timeAnimation + 'ms');
        $('#' + fromPage).css('z-index', 0);
        $('#' + goToPage).css('z-index', 90);

        this.state.myChildrens[goToPage].setState({ hidden: false });

        setTimeout(() => {
          $('#' + goToPage).css('animation', '');
          $('#' + goToPage).css('z-index', "");
          $('#' + fromPage).css('z-index', "");

          this.state.myChildrens[fromPage].setState({ hidden: true });
        }, timeAnimation);

      }
    } else {//חזרה בדפים Down
      if (this.listLevelPages[goToPage] === 1) {//Down from level 2 to level 1
        $('#' + goToPage).css('z-index', 0);

        this.state.myChildrens[goToPage].setState({ hidden: false });

        $('#' + fromPage).css('z-index', 90);
        $('#' + fromPage).css('animation', 'zoomOut ' + timeAnimation + 'ms');
        $('#' + fromPage).removeClass('showPage');
        setTimeout(() => {

          $('#' + fromPage).css('animation', '');
          $('#' + goToPage).css('z-index', "");
          $('#' + fromPage).css('z-index', "");

          this.state.myChildrens[fromPage].setState({ hidden: true });

        }, timeAnimation);
      } else
        if (this.listLevelPages[goToPage] === 0) {//Down from level 1 to level 0

          $('#' + goToPage).css('z-index', 0);

          this.state.myChildrens[goToPage].setState({ hidden: false });

          $('#' + fromPage).css('z-index', 90);
          $('#' + fromPage).css('animation', 'slideOutRight ' + timeAnimation + 'ms');
          $('#' + fromPage).removeClass('showPage');
          setTimeout(() => {

            $('#' + fromPage).css('animation', '');
            $('#' + goToPage).css('z-index', "");
            $('#' + fromPage).css('z-index', "");

            this.state.myChildrens[fromPage].setState({ hidden: true });

          }, timeAnimation);
        }
    }

  }
  componentWillMount() {



    window.closeOrBack = () => {
      document.getElementById("backInHeader").click();
    }

    document.addEventListener("backbutton"
      , (e) => {
        window.closeOrBack();


        //  alert("Android back button"+JSON.stringify(e));
        // navigator.app.exitApp();
      }
      , false);

  }


  render() {
    const fthis = this;

    let full_Height = window.innerHeight;
    let header_Height = 85;

    let body_Height = full_Height - header_Height;

    return (
      [<div id="backInHeader"
        onClick={() => {
          fthis.ChangePage(this.state.fromPage, this.state.toPage)
        }}></div>

        , <div className="body" style={{ width: window.innerWidth, height: full_Height }}>
        {[
          /* //--header--// */
          <div className="header" style={{ height: header_Height }}>
            <div id="w-node-1780bf9f7c76-4d731c10"><div href="#" className="button w-hidden-main w-button">לשידור החי</div></div>
            <div id="w-node-c52778fc92bb-4d731c10">
              <div className="logo-img-div"><img src={logo_blue} alt="" className="logo-img w-hidden-main" /></div>
            </div>
            <CheeseburgerMenu
              isOpen={this.state.menuOpen}
              closeCallback={this.closeMenu.bind(this)}>
              <MenuContent closeCallback={this.closeMenu.bind(this)} />
            </CheeseburgerMenu>

            <HamburgerMenu
              isOpen={this.state.menuOpen}
              menuClicked={this.openMenu.bind(this)}
              width={32}
              height={24}
              strokeWidth={3}
              rotate={0}
              color='black'
              borderRadius={0}
              animationDuration={0.5}
            />
          </div>

          //* //--body--// */
          , <div>
            {/* //--home page--// */}
            <div id="home" style={{ height: body_Height }} className={
              this.state.historyPages[this.state.historyPages.length - 1] === "home"
                ? "showPage scrollPage"
                : "hiddenPage"}
            >{
                this.state.historyPages[this.state.historyPages.length - 1] === "home"
                  ? <Home myParent={this} />
                  : <div></div>
              }
            </div>

            {/* //--p2--// */}
            <div id="x" style={{ height: body_Height }} className={
              this.state.historyPages[this.state.historyPages.length - 1] === "x"
                ? "showPage scrollPage"
                : "hiddenPage"}
            >{
                this.state.historyPages[this.state.historyPages.length - 1] === "x"
                  ? <P2 myParent={this} />
                  : <div></div>
              }
            </div>
          </div>
        ]}</div>
      ]
    );
  }
}

export default App;
