import React from 'react';
import $ from 'jquery';

export default class BroadcastSchedule extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
        }

        this.name = "broadcastSchedule";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["broadcastSchedule"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }

    // componentDidMount() {

    // }


    // componentWillMount() {

    // }

    render() {
        return (
            <div>

                <div className="content_section guide">
                    <h1 className="list_title">כל התכניות</h1>
                    <div className="guide_calendar_group">
                        <a href="false" className="calendar_arrow w-inline-block">
                            <div className="w-icon-slider-right" />
                        </a>
                        <div className="calendar_day">היום</div>
                        <div>28.01.19</div>
                        <a href="false" className="calendar_arrow w-inline-block">
                            <div className="w-icon-slider-left" />
                        </a>
                    </div>
                    <List_broadcastSchedule_Day date="" />
                    <a href="false" className="add_button guide w-inline-block">
                        <div>הצג עוד</div>
                    </a>
                </div>


            </div>
        );
    }
}





class List_broadcastSchedule_Day extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            date: this.props.date,
            error: null,
            isLoaded: false,
            items: []
        }
    }

    componentDidMount() {

        const fthis = this;
        $.ajax({
            url: 'https://api.eol.co.il/radio/v1/today-schedule',
            type: 'GET',
            // async: false,
            // data: '',
            dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (results) {
                results.forEach(item => {
                    item.isLiveNow=true;
                });
                
                fthis.setState({
                    isLoaded: true,
                    items: results
                });
            },
            error: function (req, status) {//
                debugger;
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }


    componentWillMount() {

    }

    render() {

        const { error, isLoaded, items } = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;

        } else if (!isLoaded) {
            return <div>Loading...</div>;

        } else {
           
            return (
                items.map(item => (
                    <a href="false" className="guide_item w-inline-block">
                        <div className={item.isLiveNow?"guide_item_plate live":"guide_item_plate"} />
                        <div className="guide_title_group">
                            <div className="guide_item_date">
                            {((new Date(item.start)).toLocaleTimeString()).substr(0,((new Date(item.start)).toLocaleTimeString().lastIndexOf(":")))} 
                            - 
                            {((new Date(new Date().setTime(new Date(item.start).getTime() + (30 * 60 * 1000)))).toLocaleTimeString())
                                .substr(0,
                                (((new Date(new Date().setTime(new Date(item.start).getTime() + (30 * 60 * 1000)))).toLocaleTimeString())
                                .lastIndexOf(":")))} </div>
                            <div className="guide_item_name">{item.name}</div>
                        </div>
                        <div>{item.description}</div>
                    </a>
                ))
            );
        }
    }
}
