import React from 'react';
//import $ from 'jquery';
import SweetAlert from 'sweetalert2-react';

export default class Swel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showSwal: true
        }
    }
    render() {
        const result = {
            important:false
            , title: "title"
            , text: "text"
            ,html: "<h1>innerHTML</h1>"
            , type: "warning"//"warning","info","question"////,"error","success"
            , showCancelButton: true
            , showConfirmButton: true
         //   , showCloseButton:true
            , confirmButtonText: "confirmButtonText"
            , cancelButtonText: "confirmButtonText"
            , confirmButtonColor:"confirmButtonColor"
            , cancelButtonColor: "cancelButtonColor"
            , padding: ""
            , background: ""
        }
        return (
            <div>
                {/* <button style={{ width: "100px", height: "70px" }} onClick={() => { debugger; this.setState({ show: true }); }}>Alert</button> */}
                <SweetAlert

                    html={result.html}
                    title={result.title}
                    text={result.text}
                    type={result.type}
                    showCancelButton={result.showCancelButton}
                    showConfirmButton={result.showConfirmButton}
                    showCloseButton={result.showCloseButton}
                    confirmButtonText={result.confirmButtonText}
                    cancelButtonText={result.confirmButtonText}
                    confirmButtonColor={result.confirmButtonColor}
                    cancelButtonColor={result.cancelButtonColor}
                    padding={result.padding}
                    background={result.background}
                    show={this.state.showSwal}
                    onConfirm={() => this.setState({ showSwal: false })}
                />
            </div>
        );
    }
}
