import React from 'react';
import $ from 'jquery';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'


export default class Swel extends React.Component {
    constructor(props) {
        super(props);

        this.name = "swal";
        this.myApp = this.props.myApp;

        let new_myChildrens = this.myApp.state.myChildrens;
        new_myChildrens["swal"] = this;
        this.myApp.setState({
            myChildrens: new_myChildrens
        });


        this.state = {
            //showSwal: true,
            whenCloseApp: false,
        }
        this.MySwal = withReactContent(Swal);

    }
    runSwal(whenCloseApp,result) {
        //  this.setState({whenCloseApp:!whenCloseApp})
        const fthis = this;
       // const result = this.state.result;
        this.MySwal.fire({
            html: result.html
            , title: result.title
            , text: result.text
            , type: result.type
            , showCancelButton: result.showCancelButton && !whenCloseApp
            , showConfirmButton: result.showConfirmButton
            , showCloseButton: result.showCloseButton
            , confirmButtonText: result.confirmButtonText
            , cancelButtonText: result.cancelButtonText
            , confirmButtonColor: result.confirmButtonColor !== undefined ? result.confirmButtonColor : '#31b1ca'
            , cancelButtonColor: result.cancelButtonColor !== undefined ? result.cancelButtonColor : '#aaa'
            , footer:
                !whenCloseApp && result.showFooterRemindMeLater
                    ? <div onClick={() => { fthis.myApp.setState({ showSwalLater: true }); this.MySwal.clickCancel() }}>
                    {result.textFooterRemindMeLater!==undefined?result.textFooterRemindMeLater:'הזכר לי מאוחר יותר'}</div>
                    : ``
            , padding: result.padding
            , background: result.background
            , show: this.state.showSwal
            //, footer: 'Copyright 2018'
            , onOpen: () => {
                // `MySwal` is a subclass of `Swal`
                //   with all the same instance & static methods
                // MySwal.clickConfirm()
            }
        }).then((click) => {
            if (click.dismiss !== "cancel") {
                window.open(result.linkOnClickConfirmButtom);
            }
            if (whenCloseApp) {
                window.navigator.app.exitApp();
            }
            if (result.important) return fthis.runSwal();
        });
    }
    componentWillMount() {
        const fthis = this;
        if (false) {
            $.ajax({
                url: "https://api.eol.co.il/radio/v1/...",
                type: 'GET',
                dataType: "json",
                contentType: 'application/json; charset = utf-8',
                success: function (result) {
                    this.setState({
                        result: result
                    });
                    fthis.runSwal(false,result);
                },
                error: function (req, status) {//
                    //    alert("error ajax"+ req+status)
                    console.log(req, status);
                    if (fthis.state.isActive)
                        fthis.setState({
                            isLoaded: true,
                            error: true
                        });
                }
            });

        }
    }
    render() {
        return null;
    }
}



