import React from 'react';
import $ from 'jquery';
import Error from '../error';

export default class LivePlay extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            item: []
            , hasError: false
        }

        this.name = "livePlay";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["livePlay"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }
    componentDidCatch(error, info) {
        
        console.log(
            'class component error at:LivePlay. '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }



    componentDidMount() {

        const fthis = this;
        $.ajax({
            url: 'https://api.eol.co.il/radio/v1/now-playing',
            type: 'GET',
            // async: false,
            // data: '',
            //  dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (result) {
                const item = result[0];
                item.show_name = "שידור חי"//result[0].name;
                item.show_length =
                    ((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                        .substr(0,
                            (((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                                .lastIndexOf(":")))
                // ((Math.floor(parseInt(result[0].duration) / 60)).toString() > 9
                //     ? (Math.floor(parseInt(result[0].duration) / 60)).toString()
                //     : ("0" + Math.floor(parseInt(result[0].duration) / 60)).toString())
                //     + ":" + (parseInt(result[0].duration) % 60).toString() > 9
                //     ? (parseInt(result[0].duration) % 60).toString()
                //     : "0" + (parseInt(result[0].duration) % 60).toString()
                //     + ":00";
                item.show_clip_id_full_url = result[0].live_broadcast;
                item.broadcaster = result[0].presenter;
                item.show_image = result[0].image_url;
                //  item.parent_program_name="שידור חי";
                item.isLive = true;

                if (window.cordova)
                    fthis.myApp.state.player.playerPlayNew(item);
                fthis.setState({
                    isLoaded: true,/////
                    error: false,
                    item: item
                });
            },
            error: function (req, status) {//
                // debugger;
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }


    componentWillMount() {

    }

    render() {
        // const fthis = this;
        //  const myApp=this.myApp;
        const { error, isLoaded, item, hasError } = this.state;
        if (hasError) {
            return (
                <Error myApp={this.myApp} showRecomends={false} />
            );
        } else {
            if (error) {
                return <Error myApp={this.myApp} title={"תקלת תקשורת"} showRecomends={false} />;
            } else if (!isLoaded) {
                return (
                    <div className="content_section online">
                        <div className="online_close w-inline-block"><img src="images/ic_close.svg" alt="true" /></div>
                        <div className="online_time_group loadingBackgrounAnimation"
                            style={{ height: "32px", width: "70%" }}>
                        </div>
                        <div className="online_program_title loadingBackgrounAnimation"
                            style={{ height: "53px", width: "50%" }}
                        />
                        <div className="online_info_txt loadingBackgrounAnimation"
                            style={{ height: "52px", width: "90%" }}
                        />
                        <div className="online_play_group">
                            <div className="online_play_picture div-block loadingBackgrounAnimation"
                                style={{ height: "350px", width: "100%" }}>
                                <div /></div>

                        </div>
                    </div>
                );
            } else {
                return (
                    <div className="content_section online">
                        <div onClick={() => window.closeOrBack()} className="online_close w-inline-block"><img src="images/ic_close.svg" alt="true" /></div>
                        <div className="online_time_group">
                            <div className="online_blue_title">{item.name}</div>
                            <div className="online_titme">  {window.dateFormat(new Date(item.start), "HH:MM") + "-" + item.show_length}
                                {/* ((new Date(item.start)).toLocaleTimeString()).substr(0, ((new Date(item.start)).toLocaleTimeString().lastIndexOf(":"))) 
                                -
                            {((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                                    .substr(0,
                                        (((new Date(new Date().setTime(new Date(item.start).getTime() + (item.duration * 60 * 1000)))).toLocaleTimeString())
                                            .lastIndexOf(":")))}
                                            */}</div>
                        </div>
                        <h1 className="online_program_title">{item.presenter}</h1>
                        <div className="online_info_txt">{item.description}</div>
                        <div className="online_play_group">
                            <div className="online_play_picture div-block">
                                <img src={item.image_url}
                                    //  srcSet="images/untitled_1-p-500.png 500w, images/untitled_1.png 720w" sizes="100vw" 
                                    alt="true" /></div>
                            {/* <div className="online_play_button w-inline-block">
                                <img src="images/ic_play_white.svg" alt="true" /></div>
                            <div className="online_play_button pause w-inline-block">
                                <img src="images/ic_pause_white.svg" alt="true" /></div> */}
                            {/* <div className="player_seeking_bar online">
                                <div className="player_seeking_line" />
                                <div className="player_seeking_bul" />
                            </div> */}
                        </div>
                    </div>

                );
            }
        }
    }
}
