import React from 'react';
import $ from 'jquery';
import Index from "./pages/p1";
import P2 from "./pages/p2";
import P3 from "./pages/p3";

//-----styles----//
import "./css/style.css"


class App extends React.Component {

  pagesComponents = [];

  constructor(props) {
    super(props);
    this.state = {
        myChildrens: []
      , historyPage: ""
      ,screenPages: []
      , screenPageNow: "p1"
    }
    this.ChangePage = this.ChangePage.bind(this);
  }


  ChangePage(goToPage, fromPage) {
    // if (goToPage !== "index"){
    //     $('#backInHeader').ready(() => {
    //         $('#backInHeader').css('opacity', 1);
    //     });
    // }else{
    //     $('#backInHeader').ready(() => {
    //         $('#backInHeader').css('opacity', 0);
    //     });
    // }
    this.state.myChildrens.forEach((Page) => {
        Page.setState({ hidden: Page.state.name !== goToPage });
    });
    // }
    this.setState({ historyPage: goToPage });
    window.closeOrBack_btnId = 'closeOrBack_from-' + goToPage;
    window.closeOrBack = () => {
        document.getElementById(window.closeOrBack_btnId).click();
    }
}
  componentWillMount() {
  }


  render() {
    // const PageOnline = this.state.screenPage;
    return (
     [<Index myParent={this}/>]
    );
  }
}

export default App;
