import React from 'react';
import NavigatorPages from 'navigator-cordova-reactjs';

//----Pages----//
import Home from "./pages/home";
import About from "./pages/about";

import { ErrorConnection } from './error';

//----style----//
//---//
//"style.css" it's important style to mobile view (android ios); 
import "./css/style.css";
//---//

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      //
    }
    this.NavigatorPages = { historyPages: [""] };

    this.full_Height = window.innerHeight;


    this.bezy = false;


    this.media = null;
    this.MusicControls = null;

    //  this.playPause = this.playPause.bind(this);


  }

  componentDidMount() {
    //debugger

  }

  componentWillMount() {

    if (window.cordova) {

      // //---lock portrait
      // window.screen.orientation.lock('portrait');

      //--back button in android
      document.addEventListener("backbutton"
        , (e) => {
          window.closeOrBack();
        }
        , false);
    }
  }


  render() {
    // debugger
    const fthis = this;

    let nowPage = this.NavigatorPages.historyPages[this.NavigatorPages.historyPages.length - 1];
    return (
      [
        <NavigatorPages key="NavigatorPages" myApp={this}
          homePageName={"home"}
          listLevelPages={{
            "home": 0,
            "broadcastSchedule": 1,
            "itemsByCategory": 1,
            "livePlay": 9,
            "category": 2,
            "search": 8,
            "programs": 1,
            "program": 2,
          }} />

        , <div key="body" className="noScroll body">
          {[
         <div key="home_div">
              {
                nowPage === "home" || fthis.NavigatorPages.historyPages.includes("home")
                  ? <Home myParent={this} />
                  : <div>home page</div>
              }
            </div>


            , <div key="about" id="about" >{
              nowPage === "about" || fthis.NavigatorPages.historyPages.includes("about")
                ? <About myParent={this} />
                : <div>about page</div>
            }
            </div>
          ]}</div>
      ]
    );
  }
}





