export class RecomendItems extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            myParent: this.props.myParent,
            error: false,
            isLoaded: false,
            items: []
            , hasError: false
        };
    }
    componentDidCatch(error, info) {
        console.log(
            'class component error at:RecomendItems(home.js). '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    componentDidMount() {
        const fthis = this;
        $.ajax({
            url: "https://api.eol.co.il/radio/v1/hp-recommended",
            type: 'GET',
            dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (results) {
                fthis.setState({
                    isLoaded: true,
                    items: results
                });
            },
            error: function (req, status) {//
                //    alert("error ajax"+ req+status)
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }

    render() {

        const { error, isLoaded, items, hasError } = this.state;

        if (hasError) {
            return (
                <div>error!!</div>
            );
        } else {
            if (error) {
                return <div><h4>תקלת תקשורת</h4></div>;
            } else if (!isLoaded) {

                return (
                    <div>

                    </div>);
            } else {
                return (
                    <div className="recommended-div">
                    <div className="reccomanded-head-div">
                        <h3 className="heading-10 reccomanded-header w-hidden-main">המומלצים שלנו</h3>
                    </div>
                            {
                                items.map((value) => {
                                    return <RecomendItem
                                            key={value.pubDate}
                                            myParent={this}
                                          //  description={value.description}
                                            guid={value.guid}
                                            pubDate={new Date(value.pubDate)}
                                            //datetime={value.datetime}
                                            link={value.link}
                                            title={value.title}>
                                        </RecomendItem>
                                })
                            }
                    </div>
                );
            }
        }
    }
}

class RecomendItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            myParent: this.props.myParent
            , hasError: false
        }
    }
    componentDidCatch(error, info) {
        console.log(
            'class component error at: RecomendItem. '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    componentWillMount() {
        this.setState({
            myParent: this.props.myParent
        });
    }
    render() {
        if (this.state.hasError) {
            return (
                <div>error!!</div>
            );
        } else {
            // debugger;
            return (  
                <div className="reccom-info-div w-hidden-main">
                    <div className="reccomandedpic-div recommend">
                        <div className="shade-div">
                            <h6 className="heading-on-shade1 w-hidden-main">{this.props.title}</h6>
                            <h6 className="heading-on-shade2 w-hidden-main">?אחת ששומעת? • אליענה בן דוד</h6>
                        </div>
                    </div>
                    <h6 className="time-heading w-hidden-main">
                    {this.props.pubDate.getDate()
                    +"."+
                    this.props.pubDate.getMonth()
                    +"."+
                    this.props.pubDate.getFullYear().toString().subString(1,3)  } 
                    | ?? דקות</h6>
                    <p className="recommended-paragraph w-hidden-main">כמו שיעור יוגה, שמתחיל במדיטציה, בישיבה עם עיניים עצומות, כדי להרגע, לנשום, לחזור לעצמנו - כך בדיוק התוכנית מתחילה הפעם ומתוך המקום הממרוכז,</p><div href="#" className="listen-link">להאזנה</div>
                    <div className="recommended-space-div underline2 w-hidden-main" />
                </div>
          );
        }
    }
}
