import React from 'react';
import $ from 'jquery';
import Error from '../error'

export default class home extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      //myChildrens: false
    }

    this.name = "home";
    this.myApp = this.props.myParent;
    this.myParent = this.props.myParent;

    let new_myChildrens = this.myParent.state.myChildrens;
    new_myChildrens["home"] = this;
    this.myParent.setState({
      myChildrens: new_myChildrens
    });

  }
  //https://api.eol.co.il/radio/v1/categories


  componentWillMount() {
    // setTimeout(() => {
    //   alert('n');
    //   window.cordova.plugins.notification.local.schedule({
    //     title: 'My first notification',
    //     text: 'Thats pretty easy...',
    //     foreground: true
    //   });
    // }, 4500);
  }

  render() {
    const fthis = this;
    return (
      <div>
        <Slider />
        <div className="search_section">
          <div className="search_form w-form">
            <div className="w-clearfix">
              <div className="search_link w-inline-block"
                onClick={() => {
                  if ($('#inputSearch').val() !== "") {
                    fthis.myApp.ChangePage("search", "home");
                    fthis.myApp.state.myChildrens.search.play($('#inputSearch').val());
                  }
                }}>
                <img src="images/ic_search.svg" alt="" />
              </div>
              <input onKeyUp={(event) => {
                if (event.keyCode === 13) {
                  if ($('#inputSearch').val() !== "") {
                    fthis.myApp.ChangePage("search", "home");
                    fthis.myApp.state.myChildrens.search.play($('#inputSearch').val());
                  }
                }
              }} id="inputSearch" type="text" maxLength={256} required placeholder="מה תרצו לשמוע?" className="search_field w-input" />
            </div>
            {/* <div className="w-form-done">
              <div>Thank you! Your submission has been received!</div>
            </div>
            <div className="w-form-fail">
              <div>Oops! Something went wrong while submitting the form.</div>
            </div> */}
          </div>
        </div>
        <div className="content_section grey">
          <h1 className="h_slogan">האונדימנד שלנו. כל התכניות. בזמן שלך.</h1>
          <div className="h_slogan_line" />
          <div>רוצים להכיר את התוכן שלנו מקרוב?<br />סידרנו אותו בכמה קטגוריות שאולי יעניינו אותך.</div>
          <Categories myParent={fthis} myApp={fthis.myApp} />
        </div>
        <RecomendItems myParent={fthis} myApp={fthis.myApp} />
      </div>

    );
  }

}




class Slider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      isLoaded: false,
      items: []
      , hasError: false
    };
    this.myParent = this.props.myParent;
    this.myApp = this.props.myApp;
  }

  componentDidCatch(error, info) {
    console.log(
      'class component error at:Slider(home.js). '
      , new Error()
      , 'error message:', JSON.stringify(error)
      , 'info message:', JSON.stringify(info));
    this.setState({ hasError: true });
    // logErrorToMyService(error, info);
  }


  componentDidMount() {
    const fthis = this;
    $.ajax({
      url: "https://api-radio.eol.co.il/app-banner.php",
      type: 'GET',
      dataType: "json",
      contentType: 'application/json; charset = utf-8',
      success: function (results) {
        fthis.setState({
          isLoaded: true,
          items: results
        });
      },
      error: function (req, status) {//
        //    alert("error ajax"+ req+status)
        console.log(req, status);
        fthis.setState({
          isLoaded: true,
          error: true
        });
      }
    });
  }

  render() {
    const fthis = this;
    const { error, isLoaded, items, hasError } = this.state;

    if (hasError) {
      return (
        <Error showRecomends={false} myApp={fthis.myApp} />
      );
    } else {
      if (error) {
        return <Error title={"תקלת תקשורת"} showRecomends={false} myApp={fthis.myApp} />;
