import React from 'react';
import $ from 'jquery';
import Error from '../error'

export default class Category extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            myChildrens: false
            , error: false,
            isLoaded: false,
            items: []
            , couterMorItems: 0
            , hasError: false
            , categoryID: 0
            , categoryTitle: ""
            , isActive: false
            , ajax: false

            , cash: []
        }

        this.name = "category";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["category"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }


    componentDidCatch(error, info) {
        console.log(
            'class component error at:Cagegory(Cagegory.js). '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    play(categoryID, categoryTitle, categoryIcon) {

        const fthis = this;

        if (fthis.state.cash[categoryID] === undefined) {
            fthis.setState({
                categoryID: categoryID
                , categoryTitle: categoryTitle
                , categoryIcon: categoryIcon
                , isActive: true
                , ajax: $.ajax({
                    url: "https://api.eol.co.il/radio/v1/categories/" + categoryID,
                    type: 'GET',
                    dataType: "json",
                    contentType: 'application/json; charset = utf-8',
                    success: function (results) {
                        debugger
                        let newCash = fthis.state.cash;
                        newCash[categoryID] = results;
                        if (fthis.state.isActive)
                            fthis.setState({
                                isLoaded: true,
                                items: results,
                                cash: newCash
                            });
                    },
                    error: function (req, status) {//
                        //    alert("error ajax"+ req+status)
                        console.log(req, status);
                        if (fthis.state.isActive)
                            fthis.setState({
                                isLoaded: true,
                                error: true
                            });
                    }
                })
            });
        } else {
            fthis.setState({
                isLoaded: true,
                items: fthis.state.cash[categoryID]
            });
        }

    }


    render() {

        const fthis = this;
        const { error, isLoaded, items, hasError, categoryTitle, couterMorItems, categoryIcon } = this.state;

        if (hasError) {
            return (
                <Error myApp={fthis.myApp} showRecomends={false} />
            );
        } else {
            if (error) {
                return <Error myApp={fthis.myApp} title={"תקלת תקשורת"} showRecomends={false} />;
            } else if (!isLoaded) {
                return (

                    <div className="content_section w-clearfix">
                        <div className="top_title_group category">
                            <div className="top_title_txt category">{categoryTitle}</div>
                            <div className="cat_link_icon list"><img src={categoryIcon} alt={categoryTitle} /></div>
                        </div>
                        <div className="item_group w-clearfix" style={{ width: "100%" }}>
                            <div className="item_pict_group loadingBackgrounAnimation">
                                <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                <div className="item_pict_infogroup">
                                    <h3 className="loadingBackgrounAnimation"> </h3>
                                    <div className="item_cat_group">
                                        <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                        <div className="item_cat_bullet" />
                                        <div className="loadingBackgrounAnimation"> </div>
                                    </div>
                                </div>
                            </div>
                            <div className="item_date_group">
                                <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                <div className="item_time_group">
                                    <div className="item_time loadingBackgrounAnimation">
                                        00
                                    </div>
                                    <div>דקות</div>
                                </div>
                            </div>
                            <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                            </div><div className="item_play_link">להאזנה</div>
                            <div className="item_social_group"><div className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></div></div>
                        </div>
                        <div className="item_group w-clearfix" style={{ width: "100%" }}>
                            <div className="item_pict_group loadingBackgrounAnimation">
                                <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                <div className="item_pict_infogroup">
                                    <h3 className="loadingBackgrounAnimation"> </h3>
                                    <div className="item_cat_group">
                                        <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                        <div className="item_cat_bullet" />
                                        <div className="loadingBackgrounAnimation"> </div>
                                    </div>
                                </div>
                            </div>
                            <div className="item_date_group">
                                <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                <div className="item_time_group">
                                    <div className="item_time loadingBackgrounAnimation">
                                        00
                                    </div>
                                    <div>דקות</div>
                                </div>
                            </div>
                            <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                            </div><div className="item_play_link">להאזנה</div>
                            <div className="item_social_group"><div className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></div></div>
                        </div>
                        <div className="item_group w-clearfix" style={{ width: "100%" }}>
                            <div className="item_pict_group loadingBackgrounAnimation">
                                <div className="item_pict_link w-inline-block"></div>    {/* a */}
                                <div className="item_pict_infogroup">
                                    <h3 className="loadingBackgrounAnimation"> </h3>
                                    <div className="item_cat_group">
                                        <div className="item_cat_link loadingBackgrounAnimation"> </div>
                                        <div className="item_cat_bullet" />
                                        <div className="loadingBackgrounAnimation"> </div>
                                    </div>
                                </div>
                            </div>
                            <div className="item_date_group">
                                <div className="loadingBackgrounAnimation" style={{ width: "55px" }}> <h3> </h3> </div>
                                <div className="item_time_group">
                                    <div className="item_time loadingBackgrounAnimation">
                                        00
                                    </div>
                                    <div>דקות</div>
                                </div>
                            </div>
                            <div className="item_txt loadingBackgrounAnimation" style={{ width: "100%", height: "100px", marginRight: "0" }}>
                            </div><div className="item_play_link">להאזנה</div>
                            <div className="item_social_group"><div className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></div>
                                <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></div></div>
                        </div>
                    </div>
                );
            } else {

                if (items.length === 0) { return (<Error myApp={fthis.myApp} title="לא נמצאו תוצאות לבקשתך" code={404} showRecomends={true} />) }
                else
                    return (
                        <div>
                            <div className="content_section w-clearfix" >
                                {/* style={{ paddingBottom: fthis.myParent.state.playerOpen ? "160px" : "75px" }}> */}
                                <div className="top_title_group category">
                                    <div className="top_title_txt category">{categoryTitle}</div>
                                    <div className="cat_link_icon list"><img src={categoryIcon} alt={categoryTitle} /></div>
                                </div>
                                {items.slice(0, 5 * couterMorItems + 5).map(item =>
                                    <Item myParent={fthis} myApp={fthis.myApp} item={item} />
                                )
                                }
                                {((5 * couterMorItems + 5) <= items.length) &&
                                    <div className="add_button w-inline-block">
                                        <div onClick={() => fthis.setState({ couterMorItems: fthis.state.couterMorItems + 1 })}>עוד שידורים</div>
                                    </div>
                                }
                            </div>
                        </div>


                    );
            }
        }

    }
}



class Item extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            hasError: false
        }
        this.myParent = this.props.myParent;
        this.myApp = this.props.myApp;
    }
    componentDidCatch(error, info) {
        console.log(
            'class Item error at: category.js '
            , new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }


    componentWillMount() {

    }
    render() {

        const fthis = this;
        if (this.state.hasError) {
            return (
                <div>error!!</div>
            );
        } else {
            const { item } = this.props;
            return (
                <div key={item.show_id} className="item_group w-clearfix">
                    <div className="item_pict_group" style={{ backgroundImage: 'url(' + item.show_image + ')' }}>
                        <div className="item_pict_link w-inline-block"></div>    {/* a */}
                        <div className="item_pict_infogroup">
                            <h3>{item.show_name}</h3>
                            <div className="item_cat_group">
                                <div className="item_cat_link">{item.parent_program_name}</div>
                                <div className="item_cat_bullet" />
                                <div>{item.broadcaster}</div>
                            </div>
                        </div>
                    </div>
                    <div className="item_date_group">
                        <div>{item.show_pub_date}</div>
                        <div className="item_time_group">
                            <div className="item_time">
                                {Math.round((parseFloat(item.show_length.substring(0, 2)) * 60) + parseFloat(item.show_length.substring(3, 5) + "." + item.show_length.substring(6, 9)))}
                            </div>
                            <div>דקות</div>
                        </div>
                    </div>
                    <div className="item_txt" dangerouslySetInnerHTML={{ __html: item.show_description }}>
                    </div><div onClick={() => fthis.myApp.playerPlayNew(item)} className="item_play_link">להאזנה</div>
                    <div className="item_social_group"><div className="social_link w-inline-block"><img src="images/ic_facebook.svg" alt="" /></div>
                        <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_twitter.svg" alt="" /></div>
                        <div className="social_line" /><div className="social_link w-inline-block"><img src="images/ic_whatsapp.svg" alt="" /></div></div>
                </div>
            )
        }
    }
}
