import React from 'react';
import $ from 'jquery';

export default class LivePlay extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
            ,hasError:false
        }

        this.name = "livePlay";
        this.myApp = this.props.myParent;
        this.myParent = this.props.myParent;

        let new_myChildrens = this.myParent.state.myChildrens;
        new_myChildrens["livePlay"] = this;
        this.myParent.setState({
            myChildrens: new_myChildrens
        });

    }
    componentDidCatch(error, info) {
        console.log(
            'class component error at:LivePlay. '
            ,new Error()
            , 'error message:', JSON.stringify(error)
            , 'info message:', JSON.stringify(info));
        this.setState({ hasError: true });
        // logErrorToMyService(error, info);
    }



    componentDidMount() {
        
        const fthis = this;
        $.ajax({
            url: 'http://192.168.0.135:5000/?url=http://api.eol.co.il/radio/v1/now-playing/',
            type: 'POST',
            async: false,
            data: JSON.stringify({ json_newUser: JSON.stringify(window.user) }),
            dataType: "json",
            contentType: 'application/json; charset = utf-8',
            success: function (results) {  
                debugger;          
                fthis.setState({
                    isLoaded: true,
                    items: results
                });
            },
            error: function (req, status) {//
                debugger;
                console.log(req, status);
                fthis.setState({
                    isLoaded: true,
                    error: true
                });
            }
        });
    }


    componentWillMount() {

    }

    render() {
        return (
            <div className="content_section online">
            <div onClick={()=>window.closeOrBack()} className="online_close w-inline-block"><img src="images/ic_close.svg" alt="true" /></div>
                <div className="online_time_group">
                    <div className="online_blue_title">עכשיו בשידור חי</div>
                    <div className="online_titme">9:30 - 11:00</div>
                </div>
                <h1 className="online_program_title">פה זה טוב</h1>
                <div className="online_info_txt">עכשיו בשידור חי עכשיו בשידור חי עכשיו בשידור חי</div>
                <div className="online_play_group">
                    <div className="online_play_picture div-block">
                    <img src="images/untitled_1.png" srcSet="images/untitled_1-p-500.png 500w, images/untitled_1.png 720w" sizes="100vw" alt="true" /></div>
                    <a href="false" className="online_play_button w-inline-block">
                    <img src="images/ic_play_white.svg" alt="true" /></a>
                    <a href="false" className="online_play_button pause w-inline-block">
                    <img src="images/ic_pause_white.svg" alt="true" /></a>
                    <div className="player_seeking_bar online">
                        <div className="player_seeking_line" />
                        <div className="player_seeking_bul" />
                    </div>
                </div>
            </div>

        );
    }
}
