import React from 'react';
//import $ from 'jquery';
import Swal from 'sweetalert2-react';

export default class Swel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showSwal: true
            , result: {
                important: true//קובע אם המשתמש יוכל לדלג ולהמשך או באפליקציה או שיהיה חייב לעדכן גירסה
                , title: "title"
                , text: "text"
                , html: `<h1 style="color:red;">innerHTML</h1>`
                , type: "warning"//"warning","info","question"////,"error","success"
                , showCancelButton: true
                , showConfirmButton: true
                , confirmButtonText: "confirmButtonText"
                , cancelButtonText: "confirmButtonText"
                , confirmButtonColor: "confirmButtonColor"
                , cancelButtonColor: "cancelButtonColor"
                , padding: ""
                , background: ""
            }
        }
        this.onConfirm = this.onConfirm.bind(this);
    }
    onConfirm(x) {
        debugger
        this.setState({ showSwal: true });
    }
    componentWillMount() {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You will not be able to recover this imaginary file!',
            type: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'No, keep it'
          }).then((result) => {
            if (result.value) {
              Swal.fire(
                'Deleted!',
                'Your imaginary file has been deleted.',
                'success'
              )
            // For more information about handling dismissals please visit
            // https://sweetalert2.github.io/#handling-dismissals
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              Swal.fire(
                'Cancelled',
                'Your imaginary file is safe :)',
                'error'
              )
            }
          })
    }
