import React from 'react';
import $ from 'jquery';
import CheeseburgerMenu from 'cheeseburger-menu';
import HamburgerMenu from 'react-hamburger-menu';
import MenuContent from './menuContent';

//--Pages
import Home from "./pages/home";
import BroadcastSchedule from './pages/broadcastSchedule';
import ItemsByCategory from './pages/itemsByCategory';
import LivePlay from './pages/livePlay';
import Category from './pages/category';
import Programs from './pages/programs';
import Program from './pages/program';

//-----styles----//
import './css/animate.css';
import "./css/style.css";

//----images---//

let interval = null;

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , screenPages: []
      , historyPages: ["home"]
      , menuOpen: false

      , playerOpen: false
      , playerPlay: false
      , playerItem: null
      , playerIsLive: false
      , playerTime: 0//ms
      , playerLoaded: false
      , playerSeekingTouchDown: false
      , playerSeekingPosition: 0
    }

    this.playerTouchStartPosition = 0;
    this.playerTouchStartMove = false;

    this.bezy = false;
    this.listLevelPages = [];
    this.listLevelPages["home"] = 0;
    this.listLevelPages["broadcastSchedule"] = 1;
    this.listLevelPages["itemsByCategory"] = 1;
    this.listLevelPages["livePlay"] = 9;
    this.listLevelPages["category"] = 2;
    this.listLevelPages["programs"] = 1;
    this.listLevelPages["program"] = 2;


    this.ChangePage = this.ChangePage.bind(this);
    //  this.playPause = this.playPause.bind(this);


  }

  playerPlayNew(playItem) {

    let fthis = this;

    this.setState({
      playerOpen: true
      //, playerPlay: true
      , playerTime: 0
      , playerItem: playItem
    });
    if (window.media !== undefined) {
      clearInterval(interval);

      window.media.pause();
      window.media.stop();
      window.media.release();
      window.media.src = playItem.show_clip_id_full_url;

    } else {
      window.media = new window.Media(playItem.show_clip_id_full_url
        , function () { alert('mediaSuccess') }
        , function (mediaError) { alert('mediaError:' + JSON.stringify(mediaError)) }
        , function (mediaStatus) {
         alert('mediaStatus' + JSON.stringify(mediaStatus));
          if (mediaStatus === 4)//stop
          {
            fthis.setState({
              playerPlay: false
              , playerLoaded: true
              , playerTime: 0
            });
            clearInterval(interval);
          }
          else if (mediaStatus === 3) {//pause
            $('.player_button').css('-webkit-animation', '');
            $('.player_button').css('animation', '');
            fthis.setState({
              playerPlay: false
              , playerLoaded: true
            });
            clearInterval(interval);
          }
          else if (mediaStatus === 2) {//play
            $('.player_button').css('-webkit-animation', '');
            $('.player_button').css('animation', '');
            fthis.setState({
              playerPlay: true
              , playerLoaded: true
            });
            fthis.setIntervalTimePlayer();
          }
          else if (mediaStatus === 1) {//start to loading
            $('.player_button').css('-webkit-animation', 'spin 2s linear infinite');
            $('.player_button').css('animation', 'spin 2s linear infinite;');
            fthis.setState({
              playerLoaded: false
              , playerPlay: false
            });
          }
        }
      );

      // window.MusicControls.create({
      //   track: 'Time is Running Out',		// optional, default : ''
      //   artist: fthis.state.playerItem.show_name,						// optional, default : ''
      //   album: fthis.state.playerItem.parent_program_name,     // optional, default: ''
      //   cover: fthis.state.playerItem.show_image,		// optional, default : nothing
      //   // cover can be a local path (use fullpath 'file:///storage/emulated/...', or only 'my_image.jpg' if my_image.jpg is in the www folder of your app)
      //   //			 or a remote url ('http://...', 'https://...', 'ftp://...')
      //   isPlaying: fthis.state.playerPlay,					// optional, default : true
      //   // dismissable: true,							// optional, default : false

      //   // hide previous/next/close buttons:
      //   //  hasPrev: false,		// show previous button, optional, default: true
      //   //  hasNext: false,		// show next button, optional, default: true
      //   //  hasClose: true,		// show close button, optional, default: false

      //   // iOS only, optional

      //   duration: fthis.state.playerItem.show_length, // optional, default: 0
      //   elapsed: 10, // optional, default: 0
      //   hasSkipForward: true, //optional, default: false. true value overrides hasNext.
      //   hasSkipBackward: true, //optional, default: false. true value overrides hasPrev.
      //   skipForwardInterval: 15, //optional. default: 0.
      //   skipBackwardInterval: 15, //optional. default: 0.
      //   hasScrubbing: false, //optional. default to false. Enable scrubbing from control center progress bar 

      //   // Android only, optional
      //   // text displayed in the status bar when the notification (and the ticker) are updated
      //   ticker: 'Now playing "Time is Running Out"',
      //   //All icons default to their built-in android equivalents
      //   //The supplied drawable name, e.g. 'media_play', is the name of a drawable found under android/res/drawable* folders
      //   playIcon: "./images/ic_play_blue.svg",
      //   pauseIcon: "./images/ic_pause_blue.svg",
      //   // prevIcon: 'media_prev',
      //   // nextIcon: 'media_next',
      //   // closeIcon: 'media_close',
      //   notificationIcon: './images/logo_radio_turkiz.svg'
      // }, (s) => console.log("onSuccess: ", s), (e) => console.log("onError: ", e));


    }

    window.media.play();
  }
  // playerPlayExists() {
  //   window.media.play();
  // }
  // playerPause() {
  // window.media.pause();
  // }
  // playerStop() {
  // window.media.stop();
  // }

  playerTouchEnd() {
    this.setState({ playerSeekingTouchDown: false });
    console.log('onTouchEnd');

    if (!this.playerTouchStartMove) {
      const newPositionPercent = (this.playerTouchStartPosition-$($('.player_seeking_bar')).offset().left) / (
        (parseInt($('.player_seeking_bar').css('width').substring(0, $('.player_seeking_bar').css('width').indexOf("px")))));

        const show_length = ((parseFloat(this.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
        + (parseFloat(this.state.playerItem.show_length.substring(3, 5) * 60)
          + parseFloat(this.state.playerItem.show_length.substring(6, 9)))
      ) * 1000;

      const newTime = show_length * newPositionPercent;
      if (newTime <= show_length && newTime >= 0) {
        this.setState({
          playerTime: newTime
        });
      }
    }

    this.playerTouchStartMove=false;


     window.media.seekTo(this.state.playerTime);

    // const newPositionPercent = Math.round(($('.player_seeking_bul')[0].offsetLeft) / (parseInt($('.player_info_group').css('width').substring(0, $('.player_info_group').css('width').indexOf("px")))) * 100);

    // this.setState({
    //   playerTime: (
    //     (parseFloat(this.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
    //     + (parseFloat(this.state.playerItem.show_length.substring(3, 5) * 60)
    //       + parseFloat(this.state.playerItem.show_length.substring(6, 9)))
    //   ) * (newPositionPercent / 100)
    // });
  }
  playerTouchMove(event) {
    this.playerTouchStartMove = true;
    const newPositionPercent = (event.touches[0].clientX - this.playerTouchStartPosition) / (
      (parseInt($('.player_info_group').css('width').substring(0, $('.player_info_group').css('width').indexOf("px")))));
    // console.log('x:', event.touches[0].clientX, ' y:', event.touches[0].clientY)
    const show_length = ((parseFloat(this.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
      + (parseFloat(this.state.playerItem.show_length.substring(3, 5) * 60)
        + parseFloat(this.state.playerItem.show_length.substring(6, 9)))
    ) * 1000;
    const newTime = show_length * newPositionPercent + this.state.playerTime;
    if (newTime <= show_length && newTime >= 0) {
      this.setState({
        playerTime: newTime
      });
      this.playerTouchStartPosition = event.touches[0].clientX;
    }
    else {
      if (newTime >= show_length) {
        this.setState({
          playerTime: show_length
        });
      } else {
        this.setState({
          playerTime: 0
        });
      }
    }
  }

  setIntervalTimePlayer() {
    let fthis = this;
    interval = setInterval(function () {
      if (fthis.state.playerTime / 1000 <= (
        (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
        + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
          + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
      )) {
        fthis.setState({
          playerTime: fthis.state.playerTime + 100
        });
      } else {
        clearInterval(interval);
        window.media.pause();
        window.media.stop();
        //הקטע הסתיים
      }
    }, 100);
  }

  openMenu() {
    this.setState({ menuOpen: true });
  }
  closeMenu() {
    this.setState({ menuOpen: false });
  }

  //----navigator and animation----///
  funAnimationIn1(goToPage, fromPage) {
    //--נכנסים דף פנימה Up--//
    let callbackFun = () => {
      this.funAnimationIn2(goToPage, fromPage);
      document.getElementById(goToPage).removeEventListener("webkitAnimationEnd", callbackFun);
    };
    document.getElementById(goToPage).addEventListener("webkitAnimationEnd", callbackFun);

    this.bezy = true;
    $('#' + goToPage).removeClass('hiddenPage');
    $('#' + goToPage).addClass('scrollPage showPage');
    $('#' + fromPage).css('z-index', 0);
    $('#' + goToPage).css('z-index', 89);
  }
  funAnimationIn2(goToPage, fromPage) {
    $('#' + fromPage).css('z-index', "");
    $('#' + goToPage).css('z-index', "");
    $('#' + goToPage).css('animation', '');
    $('#' + fromPage).removeClass('showPage');
    $('#' + fromPage).removeClass('scrollPage');
    $('#' + fromPage).addClass('hiddenPage');
    this.bezy = false;
  }

  funAnimationOut1(goToPage, fromPage) {
    //--חזרה בדפים Down--// 

    let callbackFun = () => {
      this.funAnimationOut2(goToPage, fromPage);
      document.getElementById(fromPage).removeEventListener("webkitAnimationEnd", callbackFun);
    };
    document.getElementById(fromPage).addEventListener("webkitAnimationEnd", callbackFun);
    this.bezy = true;
    $('#' + goToPage).css('z-index', 0);
    $('#' + fromPage).css('z-index', 89);
    $('#' + goToPage).removeClass('hiddenPage');
    $('#' + goToPage).addClass('scrollPage showPage');
  }
  funAnimationOut2(goToPage, fromPage) {
    $('#' + fromPage).css('animation', '');
    $('#' + goToPage).css('z-index', "");
    $('#' + fromPage).css('z-index', "");
    $('#' + fromPage).removeClass('showPage');
    $('#' + fromPage).removeClass('scrollPage');
    $('#' + fromPage).addClass('hiddenPage');
    this.bezy = false;
  }


  ChangePage(goToPage, fromPage) {

    if (!this.bezy) {
      const fthis = this;

      if (fromPage == null) {
        fromPage = "" + this.state.historyPages[this.state.historyPages.length - 1] + "";
      }

      //--זמן האנימציה
      const timeAnimation = 250;//250;//ms

      if (goToPage !== fromPage) {
        //---ניהול חזרות----//
        this.bezy = true;
        //סיום האפליקציה, סגור
        if (this.state.historyPages.length === 1
          && goToPage === undefined) {
          window.navigator.app.exitApp();
          this.bezy = false;
          return;
        } else {
          ///שמור היסטוריה
          let new_historyPages = this.state.historyPages;

          if (this.listLevelPages[goToPage] >= this.listLevelPages[fromPage]) {
            //נכנסים פנימה, הוסף להסטוריה
            new_historyPages.push(goToPage);
          } else {
            //חוזרים אחורה, מחק את הדף האחרון מההיסטוריה.
            new_historyPages.splice(new_historyPages.length - 1, 1);
          }
          //שמירת שינויים בהיסטוריה
          this.setState({
            historyPages: new_historyPages
          });
        }

        //----navigator and animation----///

        if (this.listLevelPages[goToPage] > this.listLevelPages[fromPage]) {
          //--נכנסים דף פנימה Up--//
          this.funAnimationIn1(goToPage, fromPage);

          if (this.listLevelPages[goToPage] === 1) {
            //Up from level 0 to level 1
            $('#' + goToPage).css('animation', 'slideInRight ' + timeAnimation + 'ms');

          } else { //else if (this.listLevelPages[goToPage] === 2) {
            //Up from level 1 to level 2
            $('#' + goToPage).css('animation', 'zoomIn ' + timeAnimation + 'ms');
          }
        } else {
          //--חזרה בדפים Down--//   
          this.funAnimationOut1(goToPage, fromPage);
          if (this.listLevelPages[fromPage] === 1) {
            //Down from level 1 to level 0
            $('#' + fromPage).css('animation', 'slideOutRight ' + timeAnimation + 'ms');
          }
          else { //else if (this.listLevelPages[goToPage] === 1) {
            //Down from level 2 to level 1
            $('#' + fromPage).css('animation', 'zoomOut ' + timeAnimation + 'ms');

          }
        }
        // //עיצוב כפתור חזרה
        // if (goToPage === "home") {
        //     $('#backInHeader').css('display', "none");
        // } else {
        //     $('#backInHeader').css('display', "flex");
        // }
        if (this.listLevelPages[goToPage] < this.listLevelPages[fromPage]) {
          if (fromPage === "category") {
            // $('#').scrollTop(0);
            setTimeout(() => {
              fthis.state.myChildrens.category.setState({
                error: false,
                isLoaded: false,
                items: []
                , hasError: false
                , categoryID: 0
              });
            }
              , timeAnimation);
          }
          if (fromPage === "program") {
            // $('#').scrollTop(0);
            setTimeout(() => {
              fthis.state.myChildrens.program.setState({
                error: false,
                isLoaded: false,
                items: [],
                couterMorItems: 0,
                hasError: false,
                program: null
              });
            }
              , timeAnimation);
          }
        }

        this.closeMenu();



        //  window.location.href = window.location.href + "#1";
      }
    }
  }

  componentWillMount() {

    window.closeOrBack = () => {
      document.getElementById("backInHeader").click();
    }

    //---נעילת מסך
    window.screen.orientation.lock('portrait');

    //--כתור חזרה לאנדרואיד
    document.addEventListener("backbutton"
      , (e) => {
        window.closeOrBack();


        //  alert("Android back button"+JSON.stringify(e));
        // navigator.app.exitApp();
      }
      , false);

  }


  render() {
    const fthis = this;
    let full_Height = window.innerHeight;
    let header_Height = 60;
    let bottom_Height = this.state.playerOpen ? 84 : 0;

    let body_Height = full_Height - (header_Height + bottom_Height);

    let nowPage = this.state.historyPages[this.state.historyPages.length - 1];
    return (
      [<div id="backInHeader" key="backInHeader"
        onClick={() => {
          fthis.ChangePage(this.state.historyPages[this.state.historyPages.length - 2]
            , this.state.historyPages[this.state.historyPages.length - 1])
        }}></div>

        , <div key="body" className="noScroll body" style={{ width: window.innerWidth, height: full_Height + "px" }}>
        {[
          /* //--header--// */
          <div key="top_section" className="noScroll top_section" style={{ height: header_Height + "px" }}>
            <div className="noScroll navbar w-nav">
              <CheeseburgerMenu
                topOffset={header_Height}
                bottomOffset={bottom_Height + 0.5}
                right={true}
                width={230}
                isOpen={this.state.menuOpen}
                closeCallback={this.closeMenu.bind(this)}>
                <MenuContent myParent={this} closeCallback={this.closeMenu.bind(this)} />
              </CheeseburgerMenu>
              <div style={{ height: header_Height }} className="hamburger">
                <HamburgerMenu
                  isOpen={this.state.menuOpen}
                  menuClicked={
                    this.state.menuOpen
                      ? this.closeMenu.bind(this)
                      : this.openMenu.bind(this)}
                  width={27}
                  height={20}
                  strokeWidth={2.7}
                  rotate={0}
                  color='#31b1ca'
                  borderRadius={0}
                  animationDuration={0.5}
                />
              </div>
            </div>
            <div onClick={() => { window.location.reload(); }} className="top_logo w-inline-block">
              {/* fthis.ChangePage('home', null); fthis.setState({ historyPages: ["home"] });  */}
              <img src="images/logo_radio_turkiz.svg" alt="" /></div>
            <div onClick={() => this.ChangePage("livePlay", null)} className="top_live_button w-inline-block">
              <div>לשידור החי</div>
            </div>
          </div>


          //* //--body--// */
          , <div key="body_div">
            {/* //--home page--// */}
            <div key="home" id="home" className="showPage scrollPage" style={{ height: body_Height, top: header_Height }}>
              {
                nowPage === "home" || fthis.state.historyPages.includes("home")
                  ? <Home myParent={this} />
                  : <div></div>
              }
            </div>

            {/* //--schedule--// */}
            <div key="broadcastSchedule" id="broadcastSchedule" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "broadcastSchedule" || fthis.state.historyPages.includes("broadcastSchedule")
                  ? <BroadcastSchedule myParent={this} />
                  : <div></div>
              }
            </div>

            {/* itemsByCategory */}
            <div key="itemsByCategory" id="itemsByCategory" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "itemsByCategory" || fthis.state.historyPages.includes("itemsByCategory")
                  ? <ItemsByCategory myParent={this} />
                  : <div></div>
              }
            </div>
            {/* livePlay */}
            <div key="livePlay" id="livePlay" style={{ backgroundColor: "#f4f4f4", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                nowPage === "livePlay" || fthis.state.historyPages.includes("livePlay")
                  ? <LivePlay myParent={this} />
                  : <div></div>
              }
            </div>
            {/* Programs */}
            <div key="programs" id="programs" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }}
              className="hiddenPage">{
                (nowPage === "programs" || fthis.state.historyPages.includes("programs")) &&
                <Programs myParent={this} />
              }
            </div>

            {/* category */}
            <div key="category" id="category" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
              <Category myParent={this} />
            </div>

            {/* program */}
            <div key="program" id="program" style={{ backgroundColor: "#fff", height: body_Height, top: header_Height }} className="hiddenPage">
              <Program myParent={this} />
            </div>

          </div>


          //* //--footer player--// */
          ,
          <div key="player_section" className="player_section" style={{ height: bottom_Height + "px", display: this.state.playerOpen ? "flex" : "none" }}>
            <div className="player_info_group">
              <div>{fthis.state.playerItem !== null
                ? fthis.state.playerItem.show_name
                : ""}</div>
              <div className="player_seeking_bar"
                onTouchMove={(event) => fthis.playerTouchMove(event)}
                onTouchEnd={(event) => { fthis.playerTouchEnd(); }}
                onTouchStart={(event) => { fthis.playerTouchStartPosition = event.touches[0].clientX; fthis.setState({ playerSeekingTouchDown: true }); console.log('onTouchStart'); }}
              >
                <div className="player_seeking_line" style={{
                  width: fthis.state.playerItem !== null
                    ? ((fthis.state.playerTime / 1000) / (
                      (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                      + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
                        + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
                    ) * 100).toString() + "%"
                    : 0
                }} />
                <div className="player_seeking_bul"
                  // onTouchMove={(event) => fthis.playerTouchMove(event)}
                  // onTouchStart={(event) => { fthis.playerTouchStartPosition = event.touches[0].clientX; fthis.setState({ playerSeekingTouchDown: true }); console.log('onTouchStart'); }}
                  // onTouchEnd={() => { fthis.playerTouchEnd(); }}
                  style={{
                    marginLeft: fthis.state.playerItem !== null
                      ? ((fthis.state.playerTime / 1000) / (
                        (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                        + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
                          + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
                      ) * 100).toString() + "%"
                      : 0
                    , width: fthis.state.playerSeekingTouchDown ? '7.0vw' : '2.8vw'
                    , height: fthis.state.playerSeekingTouchDown ? '7.0vw' : '2.8vw'
                  }} />
              </div>
              <div>{(!fthis.state.playerIsLive && fthis.state.playerItem !== null)
                ? (
                  (

                    ((Math.floor((fthis.state.playerTime / (60 * 60 * 1000)) % 60)) <= 9
                      ? ("0" + (Math.floor((fthis.state.playerTime / (60 * 60 * 1000)) % 60).toString()))
                      : (Math.floor((fthis.state.playerTime / (60 * 60 * 1000)) % 60)).toString()
                    ) + ":"
                  )
                  + ((Math.floor((fthis.state.playerTime / (60 * 1000)) % 60)) <= 9
                    ? ("0" + (Math.floor((fthis.state.playerTime / (60 * 1000)) % 60)).toString())
                    : (Math.floor((fthis.state.playerTime / (60 * 1000)) % 60)).toString()
                  )
                  + ":"
                  + ((Math.floor((fthis.state.playerTime / 1000) % 60)) <= 9
                    ? ("0" + (Math.floor((fthis.state.playerTime / 1000) % 60)).toString())
                    : (Math.floor((fthis.state.playerTime / 1000) % 60)).toString()
                  )
                  + " / "
                  + fthis.state.playerItem.show_length)
                : ""}</div>
            </div>
            <div onClick={() => fthis.state.playerLoaded ?
              fthis.state.playerPlay ? window.media.pause() : window.media.play()
              : console.log('המתן לסיום הטעינה')
            }
              className={fthis.state.playerPlay ? "player_button pause w-inline-block" : "player_button w-inline-block"}>
              <img src={fthis.state.playerPlay ? "./images/ic_pause_blue.svg" : "./images/ic_play_blue.svg"}
                alt={fthis.state.playerPlay ? "puse Player" : "play Player"} />
            </div>
            {/* <div className="player_video_icon"><img src="images/video.svg" alt="" /></div> */}
          </div>

        ]}</div>
      ]
    );
  }
}





