import React from 'react';
import $ from 'jquery';
import Home from "./pages/home";
import P2 from "./pages/p2";

//-----styles----//
import "./css/style.css";
import './css/animate.css';

//----images---//
import logo_blue from './assets/images/logo-blue.svg';
import menu from './assets/images/menu.svg';


class App extends React.Component {

  pagesComponents = [];

  constructor(props) {
    super(props);
    this.state = {
      myChildrens: []
      , historyPage: ""
      , screenPages: []
      , screenPageNow: "p1"
    }

    this.ChangePage = this.ChangePage.bind(this);
  }


  ChangePage(goToPage, fromPage) {
    this.setState({
      nowPage: goToPage
    });

    if (goToPage === "home"
      && fromPage === "home")
      window.navigator.app.exitApp();

      if (goToPage !== fromPage) {//goToPage !== this.state.historyPage||

        const timeAnimation = 300;//ms

        if (goToPage === "Checkout") {
          $('.foter-menubar').css('display', 'none');
        } else {
          $('.foter-menubar').css('display', 'block');
        }

        window.closeOrBack_btnId = goToPage === "Index" ? 'closeOrBack_from-Index' : 'backInHeader';
        window.closeOrBack = () => {
          document.getElementById(window.closeOrBack_btnId).click();
        }

        //----navigator and animation--///
        if (goToPage !== "Index") {
          $('#backInHeader').ready(() => {
            $('#backInHeader').css('opacity', 1);
          });
        } else {
          $('#backInHeader').ready(() => {
            $('#backInHeader').css('opacity', 0);
          });
          window.closeOrBack = () => {
            document.getElementById('closeOrBack_from-Index').click();
          }
        }

        //   debugger
        if (goToPage === "Checkout") {//level 3 
          this.setState({ modolLayer: 3 });

          $('#' + goToPage).css('animation', 'zoomIn ' + timeAnimation + 'ms');
          $('#' + fromPage).css('z-index', 0);
          $('#' + goToPage).css('z-index', 90);

          this.state.myChildrens.forEach((Page) => {
            if (Page.state.name === goToPage)
              Page.setState({ hidden: false });
          });
          setTimeout(() => {
            $('#' + goToPage).css('animation', '');
            $('#' + goToPage).css('z-index', "");
            $('#' + fromPage).css('z-index', "");
            this.state.myChildrens.forEach((Page) => {
              if (Page.state.name !== goToPage)
                Page.setState({ hidden: true });
            });
          }, timeAnimation / 2);
        } else if (goToPage === "TicketInfo" && this.state.modolLayer === 3) {//level 2 from level 3
          this.setState({ modolLayer: 2 });

          $('#' + goToPage).css('z-index', 0);
          this.state.myChildrens.forEach((Page) => {
            if (Page.state.name === goToPage)
              Page.setState({ hidden: false });
          });

          $('#' + fromPage).css('z-index', 90);
          $('#' + fromPage).css('animation', 'zoomOut ' + timeAnimation + 'ms');
          $('#' + fromPage).removeClass('showPage');
          setTimeout(() => {

            $('#' + fromPage).css('animation', '');
            $('#' + goToPage).css('z-index', "");
            $('#' + fromPage).css('z-index', "");

            this.state.myChildrens.forEach((Page) => {
              Page.setState({ hidden: Page.state.name !== goToPage });
            });

          }, timeAnimation);
        } else if (goToPage === "TicketInfo" && this.state.modolLayer === 1) {//level 2 from level 1
          $('#' + goToPage).css('animation', 'slideInRight ' + timeAnimation + 'ms');
          this.setState({ modolLayer: 1 });

          this.state.myChildrens.forEach((Page) => {
            if (Page.state.name === goToPage)
              Page.setState({ hidden: false });
          });
          setTimeout(() => {
            $('#' + goToPage).css('animation', '');
            this.state.myChildrens.forEach((Page) => {
              if (Page.state.name !== goToPage)
                Page.setState({ hidden: true });
            });
          }, timeAnimation);
        } else if (goToPage === "Index") {
          this.setState({ modolLayer: 1 });

          $('#' + goToPage).css('z-index', 0);
          this.state.myChildrens.forEach((Page) => {
            if (Page.state.name === goToPage)
              Page.setState({ hidden: false });
          });

          $('#' + fromPage).css('z-index', 90);
          $('#' + fromPage).css('animation', 'slideOutRight ' + timeAnimation + 'ms');
          $('#' + fromPage).removeClass('showPage');
          setTimeout(() => {

            $('#' + fromPage).css('animation', '');
            $('#' + goToPage).css('z-index', "");
            $('#' + fromPage).css('z-index', "");

            this.state.myChildrens.forEach((Page) => {
              Page.setState({ hidden: Page.state.name !== goToPage });
            });

          }, timeAnimation);
        }

        // }
        //  this.setState({ historyPage: goToPage });

        ///---clear animation

      }
  }
  componentWillMount() {
  }


  render() {

    let full_Height = window.innerHeight;
    let header_Height = 85;

    let body_Height = full_Height - header_Height;

    return (
      [<button className="hiddenPage" id="closeOrBack_from-home" onClick={() => window.navigator.app.exitApp()}></button>

        , <div className="body" style={{ width: window.innerWidth, height: full_Height }}>
        {[
          /* //--header--// */
          <div className="header" style={{ height: header_Height }}>
            <div id="w-node-1780bf9f7c76-4d731c10"><div href="#" className="button w-hidden-main w-button">לשידור החי</div></div>
            <div id="w-node-c52778fc92bb-4d731c10">
              <div className="logo-img-div"><img src={logo_blue} alt="" className="logo-img w-hidden-main" /></div>
            </div>
            <div id="w-node-0f070c45dc9b-4d731c10" className="logo-img-div">
              <div className="humborger-div w-hidden-main"><img src={menu} alt="" className="image-4" /></div>
            </div>
          </div>
          /* //--home page--// */
          , <Home height={body_Height} myParent={this} hidden={false} />
          /* //--p2--// */
          , <P2 myParent={this} hidden={true} />
        ]}</div>
      ]
    );
  }
}

export default App;
