import React from 'react';
import $ from 'jquery';
import SweetAlert from 'sweetalert2-react';
//import Swal from 'sweetalert2'; 

export default class Swel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showSwal: true
            , result: {
                important: true//קובע אם המשתמש יוכל לדלג ולהמשך או באפליקציה או שיהיה חייב לעדכן גירסה
                , title: "title"
                , text: "text"
                , html: `<h1 style="color:red;">innerHTML</h1>`
                , type: "warning"//"warning","info","question"////,"error","success"
                , showCancelButton: true
                , showConfirmButton: true
                , confirmButtonText: "confirmButtonText"
                , linkOnClickConfirmButtom: "https://www.eol.co.il/"
                , cancelButtonText: "confirmButtonText"
                , confirmButtonColor: "blue"
                , cancelButtonColor: "#aaa"
                //, padding: ""
                // , background: ""
            }
        }
        this.onConfirm = this.onConfirm.bind(this);


    }
    onConfirm(x) {
        debugger
        this.setState({ showSwal: this.state.result.important });
    }
    componentWillMount() {

    }
    render() {
        const fthis = this;
        const result = this.state.result;

        $('.swal2-confirm.swal2-styled').ready(() => {
            $('.swal2-confirm.swal2-styled').click(() => {
                window.open(fthis.state.result.linkOnClickConfirmButtom);
            });
        });
        return (
            <SweetAlert
                html={result.html}
                title={result.title}
                text={result.text}
                type={result.type}
                showCancelButton={result.showCancelButton}
                showConfirmButton={result.showConfirmButton}
                showCloseButton={result.showCloseButton}
                confirmButtonText={result.confirmButtonText}
                cancelButtonText={result.confirmButtonText}
                confirmButtonColor={result.confirmButtonColor}
                cancelButtonColor={result.cancelButtonColor}
                //    padding={result.padding}
                //   background={result.background}
                show={this.state.showSwal}
                onConfirm={fthis.onConfirm.bind(this)}
                onCancel={() => { debugger; }}
            />

        );
    }
}
