import React from 'react';
import $ from 'jquery';

let interval = null;

export default class Player extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            playerOpen: false
            , playerPlay: false
            , playerItem: null
            , playerTime: 0//ms
            , playerLoaded: false
            , playerSeekingTouchDown: false
            , playerSeekingPosition: 0
            , playerMusicControls: false
            , playerHistory: localStorage.getItem("playerHistory") !== null ? JSON.parse(localStorage.getItem("playerHistory")) : false
        }

        localStorage.removeItem("playerHistory");

        this.playerTouchStartPosition = 0;
        this.playerTouchStartMove = false;

        this.media = null;
        this.MusicControls = null;

        this.myApp = this.props.myApp;
        this.myApp.setState({ player: this });
    }

    componentDidMount() {
        //debugger

