    playerTouchEnd() {
        this.setState({ playerSeekingTouchDown: false });
        console.log('onTouchEnd');

        if (!this.playerTouchStartMove) {
            const newPositionPercent = (this.playerTouchStartPosition - $($('.player_seeking_bar')).offset().left) / (
                (parseInt($('.player_seeking_bar').css('width').substring(0, $('.player_seeking_bar').css('width').indexOf("px")))));

            const show_length = ((parseFloat(this.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                + (parseFloat(this.state.playerItem.show_length.substring(3, 5) * 60)
                    + parseFloat(this.state.playerItem.show_length.substring(6, 9)))
            ) * 1000;

            const newTime = show_length * newPositionPercent;
            if (newTime <= show_length && newTime >= 0) {
                this.setState({
                    playerTime: newTime
                });
            }
        }

        this.playerTouchStartMove = false;


        window.media.seekTo(this.state.playerTime);

        // const newPositionPercent = Math.round(($('.player_seeking_bul')[0].offsetLeft) / (parseInt($('.player_info_group').css('width').substring(0, $('.player_info_group').css('width').indexOf("px")))) * 100);

