        //   ) * (newPositionPercent / 100)
        // });
    }
    playerTouchMove(event) {
        this.playerTouchStartMove = true;
        const newPositionPercent = (event.touches[0].clientX - this.playerTouchStartPosition) / (
            (parseInt($('.player_info_group').css('width').substring(0, $('.player_info_group').css('width').indexOf("px")))));
        // console.log('x:', event.touches[0].clientX, ' y:', event.touches[0].clientY)
        const show_length = ((parseFloat(this.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
            + (parseFloat(this.state.playerItem.show_length.substring(3, 5) * 60)
                + parseFloat(this.state.playerItem.show_length.substring(6, 9)))
        ) * 1000;
        const newTime = show_length * newPositionPercent + this.state.playerTime;
        if (newTime <= show_length && newTime >= 0) {
            this.setState({
                playerTime: newTime
            });
            this.playerTouchStartPosition = event.touches[0].clientX;
        }
        else {
            if (newTime >= show_length) {
                this.setState({
                    playerTime: show_length - 1000
                });
            } else {
                this.setState({
                    playerTime: 1000
                });
            }
        }
    }

    setIntervalTimePlayer() {
        // debugger
        let fthis = this;
        interval = setInterval(function () {
            if (!fthis.state.playerItem.isLive) {
                if (fthis.state.playerTime / 1000 <= (
                    (parseFloat(fthis.state.playerItem.show_length.substring(0, 2)) * 60 * 60)
                    + (parseFloat(fthis.state.playerItem.show_length.substring(3, 5) * 60)
                        + parseFloat(fthis.state.playerItem.show_length.substring(6, 9)))
                )) {
                    localStorage.setItem("playerHistory",
                        JSON.stringify({
                            item: fthis.state.playerItem
                            , time: fthis.state.playerTime
                        })
                    );
                    fthis.setState({
                        playerTime: fthis.state.playerTime + 100
                    });
                } else {
                    clearInterval(interval);
                    localStorage.removeItem("playerHistory");
                    //window.media.pause();
                    //window.media.stop();
                    //הקטע הסתיים
                    console.log('updateIsPlaying=>false from setIntervalTimePlayer (else)')
                    window.MusicControls.updateIsPlaying(false);

                }
            }
        }, 100);
    }


